// src/components-recipe/FastRecipesColumn.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function FastRecipesColumn({ allRecipes }) {
  const [fastestRecipes, setFastestRecipes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Filtrar las recetas con tiempo de cocción menor o igual a 30 minutos
    const smallTimeRecipes = allRecipes.filter((recipe) => recipe.readyInMinutes <= 30);
    setFastestRecipes(smallTimeRecipes);
  }, [allRecipes]); // Se ejecuta cada vez que las recetas cambian

  return (
    <aside className="fast-recipes">
      <h3>Fastest Recipes</h3>
      <ul>
        {fastestRecipes.map((recipe) => (
          <li
            key={recipe.id}
            onClick={() => navigate(`/recipe/${recipe.id}`)}
            className="fast-recipe-card"
          >
            <img src={recipe.image} alt={recipe.title} />
            <span>{recipe.title}</span>
            <span>
              <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
            </span>
          </li>
        ))}
      </ul>
    </aside>
  );
}

export default FastRecipesColumn;
