import React, { useState, useEffect } from "react";
import { useCart } from "../components/CartContext";

const Cart = () => {
    const { cart, removeFromCart } = useCart();
    const [ingredients, setIngredients] = useState({});
    const [servings, setServings] = useState({}); // Estado para almacenar las porciones
    const [purchasedIngredients, setPurchasedIngredients] = useState({}); // Estado para los ingredientes comprados

    const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";

    // Función para actualizar los ingredientes basados en las porciones
    const fetchIngredientsForServings = async (recipeId, numberOfServings) => {
        const URL = `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=${API_KEY}&numberOfServings=${numberOfServings}`;

        try {
            const response = await fetch(URL);
            const data = await response.json();
            const updatedIngredients = data.extendedIngredients.map((ingredient) => {
                // Calcula la cantidad de cada ingrediente según las porciones
                const adjustedAmount = ingredient.amount * numberOfServings / data.servings;
                // Redondear a un solo decimal
                const roundedAmount = Math.round(adjustedAmount * 10) / 10;
                return {
                    ...ingredient,
                    amount: roundedAmount,
                };
            });
            setIngredients((prev) => ({ ...prev, [recipeId]: updatedIngredients }));
        } catch (error) {
            console.error("Error fetching ingredients for servings:", error);
        }
    };

    // Función para manejar el cambio de porciones
    const handleServingsChange = (recipeId, increment) => {
        setServings((prev) => {
            const newServings = (prev[recipeId] || 1) + increment;
            if (newServings < 1) return prev; // No permitir menos de 1 porción

            fetchIngredientsForServings(recipeId, newServings); // Actualizar los ingredientes
            return { ...prev, [recipeId]: newServings };
        });
    };

    // Función para marcar/desmarcar ingredientes como comprados
    const toggleIngredientPurchased = (recipeId, ingredientIndex) => {
        setPurchasedIngredients((prev) => {
            const updated = { ...prev };
            if (!updated[recipeId]) updated[recipeId] = [];
            updated[recipeId][ingredientIndex] = !updated[recipeId][ingredientIndex];
            return updated;
        });
    };

    useEffect(() => {
        const fetchInitialIngredients = async (recipeId) => {
            const URL = `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=${API_KEY}`;

            try {
                const response = await fetch(URL);
                const data = await response.json();
                setIngredients((prev) => ({ ...prev, [recipeId]: data.extendedIngredients }));
                // Establecer las porciones iniciales como 1
                setServings((prev) => ({ ...prev, [recipeId]: 1 }));
            } catch (error) {
                console.error("Error fetching initial ingredients:", error);
            }
        };

        cart.forEach((recipe) => {
            if (!ingredients[recipe.id]) {
                fetchInitialIngredients(recipe.id);
            }
        });
    }, [cart]);

    return (
        <div>
            <h1>Your Cart</h1>
            {cart.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <ul>
                    {cart.map((recipe) => (
                        <li key={recipe.id}>
                            <div className="recipe-container">
                                <img src={recipe.image} alt={recipe.title} style={{ width: "100px" }} />
                                <div>
                                    <h3>{recipe.title}</h3>
                                    <button onClick={() => removeFromCart(recipe.id)}>Remove</button>
                                    <div className="servings-control">
                                        <button
                                            onClick={() => handleServingsChange(recipe.id, -1)}
                                            disabled={servings[recipe.id] === 1}
                                        >
                                            -
                                        </button>
                                        <span>Servings: {servings[recipe.id] || 1}</span>
                                        <button onClick={() => handleServingsChange(recipe.id, 1)}>+</button>
                                    </div>
                                    <h4>Ingredients:</h4>
                                    <ul>
                                        {ingredients[recipe.id]?.map((ingredient, index) => (
                                            <li key={index}>
                                                <input
                                                    type="checkbox"
                                                    checked={purchasedIngredients[recipe.id]?.[index] || false}
                                                    onChange={() => toggleIngredientPurchased(recipe.id, index)}
                                                />
                                                {ingredient.amount} {ingredient.unit} {ingredient.name}
                                            </li>
                                        )) || <p>Loading ingredients...</p>}
                                    </ul>
                                </div>
                            </div>
                            <hr style={{ margin: "20px 0", border: "1px solid #ddd" }} />
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Cart;
