import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import DOMPurify from "dompurify"; // Para limpiar el HTML si es necesario
import { useFavorites } from "../components/FavoritesContext"; // Para manejar favoritos
import { useAuth0 } from "@auth0/auth0-react"; // Para saber si el usuario está autenticado
import { useCart } from "../components/CartContext"; // Para manejar el carrito
import "./HomeDetails.css";

function HomeDetails() {
  const { id } = useParams(); // Obtener el ID de la receta desde la URL
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showLoginMessage, setShowLoginMessage] = useState(false); // Estado para mostrar el mensaje de login

  const { favorites, addFavorite, removeFavorite } = useFavorites(); // Para manejar los favoritos
  const { cart, addToCart, removeFromCart } = useCart(); // Para manejar el carrito
  const { isAuthenticated, loginWithRedirect } = useAuth0(); // Para verificar si el usuario está autenticado
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipeDetails = async () => {
      try {
        const response = await fetch(
          `https://api.spoonacular.com/recipes/${id}/information?apiKey=d0fba68ef5204602ac929844f28b7d5f`
        );
        const data = await response.json();
        setRecipe(data);
      } catch (error) {
        setError("Error fetching recipe details");
      } finally {
        setLoading(false);
      }
    };

    fetchRecipeDetails();
  }, [id]);

  // Si está cargando
  if (loading) {
    return <p>Loading...</p>;
  }

  // Si hay un error
  if (error) {
    return <p>{error}</p>;
  }

  // Limpiar el HTML de la descripción si es necesario
  const cleanSummary = DOMPurify.sanitize(recipe.summary);

  // Función para agregar/quitar de favoritos
  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  // Función para agregar/quitar del carrito
  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  return (
    <div className="recipe-details-container">
      <h1>{recipe.title}</h1>
      <img src={recipe.image} alt={recipe.title} className="recipe-image" />

      <div className="recipe-info">
        {/* Información de dietas */}
        <p>
          <b>Diet:</b> {recipe.diets.join(", ")}
        </p>

        {/* Información de alergias y etiquetas */}
        <p>
          <b>Tags:</b> {recipe.dishTypes?.join(", ")}
        </p>

        <p>
          <b>Preparation time:</b> {recipe.readyInMinutes} minutes
        </p>

        {/* Detalles de la receta */}
        <h2>Description</h2>
        <p
          dangerouslySetInnerHTML={{
            __html: cleanSummary,
          }}
        />

        {/* Mostrar mensaje de login si no está autenticado */}
        {showLoginMessage && (
          <div className="login-message">
            <p>You need to log in to save recipes or add them to your cart.</p>
            <div className="button-container">
              <button className="login-button" onClick={loginWithRedirect}>
                Log In
              </button>
              <button
                className="close-button"
                onClick={() => setShowLoginMessage(false)}
              >
                Close
              </button>
            </div>
          </div>
        )}

        <h3>Ingredients</h3>
        <ul>
          {recipe.extendedIngredients.map((ingredient) => (
            <li key={ingredient.id}>{ingredient.original}</li>
          ))}
        </ul>

        <h3>Instructions</h3>
        <p
          dangerouslySetInnerHTML={{
            __html: recipe.instructions,
          }}
        />

        {/* Información nutricional */}
        <h3>Nutrition Info</h3>
        <ul>
          {recipe.nutrition?.nutrients.map((nutrient) => (
            <li key={nutrient.title}>
              <b>{nutrient.title}:</b> {nutrient.amount} {nutrient.unit}
            </li>
          ))}
        </ul>

        {/* Botones de acción */}
        <div className="buttons-container">
          {/* Botón para añadir/quitar de favoritos */}
          <button
            className={`save-btn ${favorites.some((fav) => fav.id === recipe.id) ? "saved" : ""}`}
            onClick={() => toggleSaveRecipe(recipe)}
          >
            <i
              className={
                favorites.some((fav) => fav.id === recipe.id)
                  ? "bx bxs-heart"
                  : "bx bx-heart"
              }
            ></i>
            {favorites.some((fav) => fav.id === recipe.id) ? "Saved" : "Save Recipe"}
          </button>

          {/* Botón para añadir/quitar del carrito */}
          <button
            className={`details-button ${cart.some((item) => item.id === recipe.id) ? "added" : ""}`}
            onClick={(e) => addToCartHandler(recipe, e)}
          >
            <i
              className={
                cart.some((item) => item.id === recipe.id)
                  ? "bx bxs-cart-add"
                  : "bx bx-cart-add"
              }
            ></i>
            {cart.some((item) => item.id === recipe.id) ? "Remove from Cart" : "Add to Cart"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default HomeDetails;
