import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext"; // Importar el contexto
import { useAuth0 } from "@auth0/auth0-react"; // Importar el hook useAuth0
import { useCart } from "../components/CartContext"; // Importar el hook del carrito
import "./Home-Populars.css";

function HomePopulars() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showLoginMessage, setShowLoginMessage] = useState(false); // Estado para mostrar el mensaje de login

  const { favorites, addFavorite, removeFavorite } = useFavorites(); // Usa el contexto de favoritos
  const { cart, addToCart, removeFromCart } = useCart(); // Usamos el hook de carrito
  const { isAuthenticated, loginWithRedirect } = useAuth0(); // Usamos isAuthenticated para saber si el usuario está logueado
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=16`;

  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.recipes);
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const nextRecipes = () => {
    if (currentIndex + 4 < recipes.length) {
      setCurrentIndex((prevIndex) => prevIndex + 4);
    }
  };

  const prevRecipes = () => {
    if (currentIndex - 4 >= 0) {
      setCurrentIndex((prevIndex) => prevIndex - 4);
    }
  };

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true); // Mostrar el mensaje si no está logueado
      return loginWithRedirect(); // Si no está logueado, redirige a login
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id); // Si ya está en el carrito, lo elimina
    } else {
      addToCart(recipe); // Si no está, lo agrega al carrito
    }
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true); // Mostrar el mensaje si no está logueado
      return loginWithRedirect(); // Si no está logueado, redirige a login
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading recipes...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{`Error: ${error}`}</p>
      </div>
    );
  }

  return (
    <section className="container-populars">
      <div className="home-container-populars">
        {showLoginMessage && (
          <div className="login-message">
            <p>You need to log in to save recipes or add them to your cart.</p>
            <div className="button-container">
              <button
                className="login-button"
                onClick={loginWithRedirect}
              >
                Log In
              </button>
              <button
                className="close-button"
                onClick={() => setShowLoginMessage(false)}
              >
                Close
              </button>
            </div>
          </div>
        )}
        
        <div className="carousel">
          {recipes.slice(currentIndex, currentIndex + 4).map((recipe) => (
            <div
              key={recipe.id}
              className="carousel-item"
              onClick={() => navigate(`/recipe/${recipe.id}`)}
            >
              <img
                src={recipe.image}
                alt={recipe.title}
                className="carousel-image"
              />
              <div className="carousel-meta">
                <h4 className="carousel-title">{recipe.title}</h4>
                <div className="carousel-actions">
                  <button
                    className="save-btn"
                    onClick={() => toggleSaveRecipe(recipe)}
                  >
                    <i
                      className={favorites.some((fav) => fav.id === recipe.id)
                        ? "bx bxs-heart"
                        : "bx bx-heart"
                      }
                    ></i>
                  </button>

                  <button
                    className="cart-btn"
                    onClick={(e) => addToCartHandler(recipe, e)} // Agregar receta al carrito
                  >
                    <i
                      className={cart.some((item) => item.id === recipe.id)
                        ? "bx bxs-plus"
                        : "bx bx-plus"
                      }
                    ></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="carousel-navigation">
          <button onClick={prevRecipes}>Prev</button>
          <button onClick={nextRecipes}>Next</button>
        </div>
      </div>
    </section>
  );
}

export default HomePopulars;
