// src/components-recipes/RecipeList.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';

function RecipeList({ recipes, addToCartHandler, toggleSaveRecipe, favorites, cart }) {
  const navigate = useNavigate();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const [showLoginMessage, setShowLoginMessage] = useState(false);

  const handleSaveRecipe = (e, recipe) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }
    toggleSaveRecipe(recipe);
  };

  const handleAddToCart = (e, recipe) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }
    addToCartHandler(recipe, e);
  };

  const handleRecipeClick = (recipe) => {
    navigate(`/recipe/${recipe.id}`, {
      state: { fromRecipes: true }, // Indicamos que venimos de la página de recetas
    });
  };

  return (
    <div>
      {showLoginMessage && (
        <div className="login-message">
          <p>You need to log in to save recipes or add them to your cart.</p>
          <div className="button-container">
            <button
              className="login-button"
              onClick={loginWithRedirect}
            >
              Log In
            </button>
            <button
              className="close-button"
              onClick={() => setShowLoginMessage(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
      <div className="recipes-grid">
        {recipes.map((recipe) => (
          <div
            key={recipe.id}
            className="recipe-card"
            onClick={() => handleRecipeClick(recipe)} // Al hacer clic, redirigimos con estado
          >
            <div className="recipe-image1">
              <img
                src={recipe.image}
                alt={recipe.title}
                className="recipe-image"
              />
            </div>
            <div className="recipe-info">
              <h3 className="recipe-title">{recipe.title}</h3>
              <div className="recipe-meta">
                <span>
                  <i className="bx bx-time-five"></i>{' '}
                  {recipe.readyInMinutes !== undefined ? recipe.readyInMinutes : 'N/A'}{' '}
                  min
                </span>
                <button
                  className={`save-btn ${favorites.some((fav) => fav.id === recipe.id) ? 'saved' : ''}`}
                  onClick={(e) => handleSaveRecipe(e, recipe)}
                >
                  <i
                    className={favorites.some((fav) => fav.id === recipe.id) ? 'bx bxs-heart' : 'bx bx-heart'}
                  ></i>
                </button>
                <button
                  className="details-button"
                  onClick={(e) => handleAddToCart(e, recipe)}
                >
                  <i
                    className={cart.some((item) => item.id === recipe.id) ? 'bx bxs-plus' : 'bx bx-plus'}
                  ></i>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecipeList;
