import React from "react";
import { Link, useNavigate } from "react-router-dom"; // Importa useNavigate
import { useAuth0 } from "@auth0/auth0-react";
import { LoginButton } from "./Login";
import { LogoutButton } from "./Logout";
import { Profile } from "./Profile";
import logo from "../img/icon.png"; // Asumiendo que el logo está en la carpeta img
import "./Header.css";

function Header() {
    const { isAuthenticated } = useAuth0();
    const navigate = useNavigate(); // Usamos useNavigate para la redirección

    const handleFavoriteClick = () => {
        if (!isAuthenticated) {
            // Si no está autenticado, redirige a la página de login
            navigate("/login");  // Redirige a la página de login
        } else {
            // Si está autenticado, realiza la acción de favoritos
            console.log('Accediendo a favoritos...');
        }
    };

    const handleDropdownChange = (e) => {
        const value = e.target.value;  // Obtener el valor seleccionado
        if (value !== "all") {
            navigate(`/${value}`);  // Redirige a la URL correspondiente
        }
    };

    return (
        <header className="App-header">
            <div className="header-top">
                {/* Logo */}
                <img src={logo} className="App-logo" alt="logo" />

                {/* Buscador con ícono de búsqueda */}
                <div className="input-group">
                    <select className="dropdown" onChange={handleDropdownChange}>
                        <option value="all">All</option>
                        <option value="recipes">Recipes</option>
                        <option value="service">Service</option>
                        <option value="restaurants">Restaurants</option>
                    </select>
                    <input type="text" className="input" placeholder="Search..." />
                    <button className="button--submit">
                        <i className='bx bx-search'></i>
                    </button>
                </div>

                {/* Acciones del usuario (Perfil, Login, Logout, Carrito) */}
                <div className="user-actions">
                    {isAuthenticated ? (
                        <>
                            <Profile />
                            <LogoutButton />
                        </>
                    ) : (
                        <LoginButton />
                    )}
                    <button className="favorite-button" onClick={handleFavoriteClick}>
                        <i className="bx bx-heart"></i>
                    </button>
                    <button className="cart-button">
                        <i className='bx bx-receipt'></i>
                        <span className="cart-text"> Cart: 00</span>
                    </button>
                </div>
            </div>

            {/* Línea de separación añadida automáticamente por CSS */}

            {/* Navegación */}
            <nav className="navigation">
                <ul>
                    <li>
                        <Link to="/">Home</Link>
                    </li>
                    <li>
                        <Link to="/recipes">Recipes</Link>
                    </li>
                    <li>
                        <Link to="/service">Service</Link>
                    </li>
                    <li>
                        <Link to="/restaurants">Restaurants</Link>
                    </li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
