import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useAuth0 } from "@auth0/auth0-react";
import { useCart } from "../components/CartContext";
import "./Recipes.css";

function Recipes() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showLoginMessage, setShowLoginMessage] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const recipesPerPage = 9;

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const URL = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&number=100`;

  const navigate = useNavigate();

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.results);
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  const indexOfLastRecipe = currentPage * recipesPerPage;
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;
  const currentRecipes = recipes.slice(indexOfFirstRecipe, indexOfLastRecipe);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading recipes...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{`Error: ${error}`}</p>
      </div>
    );
  }

  return (
    <section className="container-populars no-index-padding">
      <div className="home-container-populars">
        <div className="home-populars-texts">
          <h1 className="home-populars-title">
            Discover our full list of recipes
          </h1>
          <p className="home-populars-subtitle">
            Explore a variety of recipes that are quick to prepare and full of flavor. Find your next favorite meal!
          </p>
        </div>

        {showLoginMessage && (
          <div className="login-message">
            <p>You need to log in to save recipes or add them to your cart.</p>
            <div className="button-container">
              <button
                className="login-button"
                onClick={loginWithRedirect}
              >
                Log In
              </button>
              <button
                className="close-button"
                onClick={() => setShowLoginMessage(false)}
              >
                Close
              </button>
            </div>
          </div>
        )}

        <div className="recipes-grid">
          {currentRecipes.map((recipe) => (
            <div
              key={recipe.id}
              className="recipe-card"
              onClick={() => navigate(`/recipe/${recipe.id}`)}
            >
              <div className="recipe-image1">
                <img
                  src={recipe.image}
                  alt={recipe.title}
                  className="recipe-image"
                />
              </div>
              <div className="recipe-info">
                <h3 className="recipe-title">{recipe.title}</h3>

                <div className="recipe-meta">
                  <span>
                    <i className="bx bx-time-five"></i>{" "}
                    {recipe.readyInMinutes !== undefined
                      ? recipe.readyInMinutes
                      : "N/A"}{" "}
                    min
                  </span>

                  <button
                    className={`save-btn ${favorites.some((fav) => fav.id === recipe.id)
                        ? "saved"
                        : ""
                      }`}
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleSaveRecipe(recipe);
                    }}
                    aria-label={
                      favorites.some((fav) => fav.id === recipe.id)
                        ? "Remove from saved recipes"
                        : "Save recipe"
                    }
                  >
                    <i
                      className={
                        favorites.some((fav) => fav.id === recipe.id)
                          ? "bx bxs-heart"
                          : "bx bx-heart"
                      }
                    ></i>
                  </button>

                  <button
                    className="details-button"
                    onClick={(e) => {
                      addToCartHandler(recipe, e);
                    }}
                    aria-label={
                      cart.some((item) => item.id === recipe.id)
                        ? "Remove from cart"
                        : "Add to cart"
                    }
                  >
                    <i
                      className={
                        cart.some((item) => item.id === recipe.id)
                          ? "bx bxs-plus"
                          : "bx bx-plus"
                      }
                    ></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="pagination">
          {Array.from(
            { length: Math.ceil(recipes.length / recipesPerPage) },
            (_, index) => (
              <button
                key={index}
                onClick={() => paginate(index + 1)}
                className={`page-btn ${currentPage === index + 1 ? "active" : ""}`}
              >
                {index + 1}
              </button>
            )
          )}
        </div>
      </div>
    </section>
  );
}

export default Recipes;
