import React, { useState } from "react";

function Filters({ onApplyFilters, onClearFilters }) {
  const [selectedCuisine, setSelectedCuisine] = useState([]);
  const [selectedMealType, setSelectedMealType] = useState([]);
  const [selectedDietary, setSelectedDietary] = useState([]);
  const [selectedCookingTime, setSelectedCookingTime] = useState([]);
  const [selectedDifficulty, setSelectedDifficulty] = useState([]);
  const [sortBy, setSortBy] = useState("");  // Nuevo estado para la ordenación

  const handleCuisineChange = (e) => {
    const value = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedCuisine(value);
    onApplyFilters({ cuisine: value || [] });
  };

  const handleMealTypeChange = (e) => {
    const value = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedMealType(value);
    onApplyFilters({ mealType: value || [] });
  };

  const handleDietaryChange = (e) => {
    const value = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedDietary(value);
    onApplyFilters({ dietary: value || [] });
  };

  const handleCookingTimeChange = (e) => {
    const value = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedCookingTime(value);
    onApplyFilters({ cookingTime: value || [] });
  };

  const handleDifficultyChange = (e) => {
    const value = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedDifficulty(value);
    onApplyFilters({ difficulty: value || [] });
  };

  // Filtro de orden
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    onApplyFilters({ sortBy: e.target.value });
  };

  const handleClearAll = () => {
    setSelectedCuisine([]);
    setSelectedMealType([]);
    setSelectedDietary([]);
    setSelectedCookingTime([]);
    setSelectedDifficulty([]);
    setSortBy("");
    onClearFilters();
  };

  return (
    <div className="filters-container">
      <h3>Filters</h3>
      
      {/* Filtro de cocina */}
      <label>
        Cuisine
        <select multiple value={selectedCuisine} onChange={handleCuisineChange}>
          {/* Aquí puedes agregar las opciones para la cocina */}
        </select>
      </label>

      {/* Filtro de tipo de comida */}
      <label>
        Meal Type
        <select multiple value={selectedMealType} onChange={handleMealTypeChange}>
          {/* Aquí puedes agregar las opciones para el tipo de comida */}
        </select>
      </label>

      {/* Filtro de dieta */}
      <label>
        Dietary
        <select multiple value={selectedDietary} onChange={handleDietaryChange}>
          {/* Aquí puedes agregar las opciones para la dieta */}
        </select>
      </label>

      {/* Filtro de tiempo de cocción */}
      <label>
        Cooking Time
        <select multiple value={selectedCookingTime} onChange={handleCookingTimeChange}>
          {/* Aquí puedes agregar las opciones para el tiempo de cocción */}
        </select>
      </label>

      {/* Filtro de dificultad */}
      <label>
        Difficulty
        <select multiple value={selectedDifficulty} onChange={handleDifficultyChange}>
          {/* Aquí puedes agregar las opciones para la dificultad */}
        </select>
      </label>

      {/* Filtro de orden */}
      <label>
        Sort By
        <select value={sortBy} onChange={handleSortChange}>
          <option value="">Select</option>
          <option value="alphabetical">Alphabetical</option>
          <option value="score">Score</option>
        </select>
      </label>

      <button onClick={handleClearAll}>Clear All Filters</button>
    </div>
  );
}

export default Filters;
