import React from "react";
import DietaryIcon from "../components/DietaryIcon"; // Asegúrate de que este archivo exista y esté correctamente ubicado
import "./HomeDetails.css"; // Si tienes un archivo CSS para estilos específicos

const HomeDetails = ({ recipe }) => {
    if (!recipe) {
        return <p>Loading recipe details...</p>;
    }

    const {
        name,
        description,
        image,
        dietaryRestrictions,
        nutritionalInfo,
        additionalInfo,
        usersAlsoLiked,
    } = recipe;

    return (
        <div className="home-details">
            {/* Título e imagen principal */}
            <h1>{name}</h1>
            <img src={image} alt={name} className="recipe-image" />

            {/* Descripción */}
            <section className="recipe-description">
                <h2>Description</h2>
                <p>{description}</p>
            </section>

            {/* Restricciones alimentarias */}
            <section className="dietary-restrictions">
                <h2>Dietary Restrictions</h2>
                <div className="dietary-icons">
                    {dietaryRestrictions && dietaryRestrictions.length > 0 ? (
                        dietaryRestrictions.map((restriction, index) => (
                            <DietaryIcon key={index} restriction={restriction} />
                        ))
                    ) : (
                        <p>No specific dietary restrictions listed.</p>
                    )}
                </div>
            </section>

            {/* Información nutricional */}
            <section className="nutritional-info">
                <h2>Nutritional Information</h2>
                {nutritionalInfo ? (
                    <ul>
                        {Object.entries(nutritionalInfo).map(([key, value]) => (
                            <li key={key}>
                                <strong>{key}: </strong>{value}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No nutritional information available.</p>
                )}
            </section>

            {/* Información adicional */}
            <section className="additional-info">
                <h2>Additional Information</h2>
                {additionalInfo ? (
                    <ul>
                        <li><strong>Servings: </strong>{additionalInfo.servings}</li>
                        <li><strong>Cost per serving: </strong>${additionalInfo.costPerServing}</li>
                        <li><strong>Calories per serving: </strong>{additionalInfo.caloriesPerServing} kcal</li>
                    </ul>
                ) : (
                    <p>No additional information available.</p>
                )}
            </section>

            {/* Usuarios también les gustó */}
            <section className="users-also-liked">
                <h2>Users Also Liked</h2>
                {usersAlsoLiked && usersAlsoLiked.length > 0 ? (
                    <div className="similar-recipes">
                        {usersAlsoLiked.map((item, index) => (
                            <div key={index} className="similar-recipe">
                                <img src={item.image} alt={item.name} />
                                <p>{item.name}</p>
                            </div>
                        ))}
                    </div>
                ) : (
                    <p>No recommendations available.</p>
                )}
            </section>
        </div>
    );
};

export default HomeDetails;
