import React from "react";
import { Link } from "react-router-dom";
import { useFavorites } from "./Favorites"; // Importar el contexto
import "./Favorites.css";

function FavoritesPage() {
  const { favorites, toggleFavorite } = useFavorites();

  if (favorites.length === 0) {
    return <p>No tienes recetas guardadas.</p>;
  }

  return (
    <section className="favorites-container">
      <h1>Mis Recetas Guardadas</h1>
      <div className="favorites-grid">
        {favorites.map((recipe) => (
          <div key={recipe.id} className="favorite-item">
            <img src={recipe.image} alt={recipe.title} />
            <h3>{recipe.title}</h3>
            <div className="favorite-meta">
              <span>
                <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
              </span>
              <button onClick={() => toggleFavorite(recipe)}>
                <i className="bx bxs-heart"></i>
              </button>
            </div>
            <Link to={`/recipe/${recipe.id}`}>Ver receta</Link>
          </div>
        ))}
      </div>
    </section>
  );
}

export default FavoritesPage;
