import React, { useState, useEffect } from "react";
import { useFavorites } from "./FavoritesContext";  // Cambiado a FavoritesContext
import { useCart } from "../components/CartContext";  // Contexto de carrito

const Favorites = () => {
    const { favorites, removeFavorite } = useFavorites();
    const { addToCart } = useCart();
    const [ingredients, setIngredients] = useState({});

    // Función para agregar receta al carrito
    const addRecipeToCart = (recipe) => {
        addToCart(recipe);
    };

    // Cargar ingredientes de las recetas en favoritos
    useEffect(() => {
        const fetchIngredients = async (recipeId) => {
            const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
            const URL = `https://api.spoonacular.com/recipes/${recipeId}/ingredientWidget.json?apiKey=${API_KEY}`;

            try {
                const response = await fetch(URL);
                const data = await response.json();
                setIngredients((prev) => ({ ...prev, [recipeId]: data.ingredients }));
            } catch (error) {
                console.error("Error fetching ingredients:", error);
            }
        };

        // Cargar ingredientes para todas las recetas favoritas
        favorites.forEach((recipe) => {
            if (!ingredients[recipe.id]) {
                fetchIngredients(recipe.id);
            }
        });
    }, [favorites, ingredients]);

    return (
        <div>
            <h1>Your Favorites</h1>
            {favorites.length === 0 ? (
                <p>You have no favorite recipes yet!</p>
            ) : (
                <ul>
                    {favorites.map((recipe) => (
                        <li key={recipe.id}>
                            <div className="recipe-container">
                                <img src={recipe.image} alt={recipe.title} style={{ width: "100px" }} />
                                <div>
                                    <h3>{recipe.title}</h3>
                                    <p>Ready in {recipe.readyInMinutes} minutes</p>
                                    <button onClick={() => removeFavorite(recipe.id)}>Remove</button>
                                    {/* Agregar al carrito */}
                                    <button onClick={() => addRecipeToCart(recipe)}>Add to Cart</button>
                                    
                                    <h4>Ingredients:</h4>
                                    <ul>
                                        {ingredients[recipe.id]?.map((ingredient, index) => (
                                            <li key={index}>
                                                {ingredient.amount.us.value} {ingredient.amount.us.unit}{" "}
                                                {ingredient.name}
                                            </li>
                                        )) || <p>Loading ingredients...</p>}
                                    </ul>
                                </div>
                            </div>
                            {/* Línea de separación */}
                            <hr style={{ margin: "20px 0", border: "1px solid #ddd" }} />
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Favorites;
