import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Home.css";

function HomePopulars() {
  const [recipes, setRecipes] = useState([]); // Estado para almacenar las recetas
  const [loading, setLoading] = useState(true); // Estado para manejar el loading
  const [error, setError] = useState(null);     // Estado para manejar errores
  const [currentIndex, setCurrentIndex] = useState(0); // Índice del primer elemento visible

  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f"; // Tu API key
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=10`; // URL para obtener 10 recetas

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.recipes); // Guarda las recetas
        } else {
          throw new Error(data.message || "Error al cargar las recetas");
        }
      } catch (error) {
        setError(error.message); // Maneja errores
      } finally {
        setLoading(false); // Desactiva el loading
      }
    };

    fetchRecipes();
  }, []);

  // Manejar el desplazamiento hacia adelante
  const nextRecipes = () => {
    if (currentIndex + 4 < recipes.length) {
      setCurrentIndex((prevIndex) => prevIndex + 4);
    }
  };

  // Manejar el desplazamiento hacia atrás
  const prevRecipes = () => {
    if (currentIndex > 0) {
      setCurrentIndex((prevIndex) => prevIndex - 4);
    }
  };

  if (loading) {
    return <p>Cargando recetas...</p>;
  }

  if (error) {
    return <p>{`Error: ${error}`}</p>;
  }

  return (
    <section className="container-populars">
      <div className="home-container-choose">
        <h1 className="home-choose-title">Recetas Destacadas</h1>
        <div className="carousel-multiple">
          <button className="carousel-btn prev" onClick={prevRecipes} disabled={currentIndex === 0}>
            &#10094;
          </button>
          <div className="carousel-track">
            {recipes.slice(currentIndex, currentIndex + 4).map((recipe) => (
              <div key={recipe.id} className="carousel-item">
                <img
                  src={recipe.image}
                  alt={recipe.title}
                  className="carousel-image"
                />
                <h3 className="carousel-title">{recipe.title}</h3>
                <Link to={`/recipe/${recipe.id}`} className="carousel-link">
                  Ver receta
                </Link>
              </div>
            ))}
          </div>
          <button
            className="carousel-btn next"
            onClick={nextRecipes}
            disabled={currentIndex + 4 >= recipes.length}
          >
            &#10095;
          </button>
        </div>
      </div>
    </section>
  );
}

export default HomePopulars;
