import React, { useEffect, useState } from "react";
import { useFavorites } from "../components/FavoritesContext"; // Usamos el contexto de favoritos
import { useCart } from "../components/CartContext"; // Usamos el contexto del carrito
import { useAuth0 } from "@auth0/auth0-react"; // Usamos el hook de auth0 para ver si está logueado
import "./FastRecipesColumn.css"; // Archivo de estilos para este componente

function FastRecipesColumn() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { favorites, addFavorite, removeFavorite } = useFavorites(); // Usamos el contexto de favoritos
  const { cart, addToCart, removeFromCart } = useCart(); // Usamos el contexto de carrito
  const { isAuthenticated, loginWithRedirect } = useAuth0(); // Hook de auth0 para ver si está logueado

  const API_KEY = "540464a4610b4e4c9488d105323ad0af"; // API Key de Spoonacular
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=5`; // Obtiene 5 recetas rápidas

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();
        if (response.ok) {
          setRecipes(data.recipes);
        } else {
          throw new Error(data.message || "No se pudieron cargar las recetas.");
        }
      } catch (error) {
        console.error(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchRecipes();
  }, []);

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      alert("Debes iniciar sesión para agregar al carrito.");
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      alert("Debes iniciar sesión para guardar recetas.");
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  return (
    <div className="fast-recipes-column">
      {loading ? (
        <p>Cargando recetas...</p>
      ) : (
        <div className="recipe-list">
          {recipes.map((recipe) => (
            <div className="recipe-card" key={recipe.id}>
              <div className="recipe-image">
                <img src={recipe.image} alt={recipe.title} />
              </div>
              <div className="recipe-info">
                <h3>{recipe.title}</h3>
                <p>{recipe.readyInMinutes} min</p>
                <div className="recipe-actions">
                  <button
                    className={`save-btn ${favorites.some(
                      (fav) => fav.id === recipe.id
                    )
                      ? "saved"
                      : ""}`}
                    onClick={() => toggleSaveRecipe(recipe)}
                  >
                    <i
                      className={`bx ${
                        favorites.some((fav) => fav.id === recipe.id)
                          ? "bxs-heart"
                          : "bx-heart"
                      }`}
                    ></i>
                  </button>
                  <button
                    className="add-to-cart-btn"
                    onClick={(e) => addToCartHandler(recipe, e)}
                  >
                    <i
                      className={`bx ${
                        cart.some((item) => item.id === recipe.id)
                          ? "bxs-cart-add"
                          : "bx-cart-add"
                      }`}
                    ></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default FastRecipesColumn;
