import React, { useEffect } from 'react';

function Pagination({
  currentPage,
  setCurrentPage,
  visiblePages,
  setVisiblePages,
  recipes,
  recipesPerPage
}) {
  const totalPages = Math.ceil(recipes.length / recipesPerPage);

  // Asegurarse de que `visiblePages` tenga un tamaño adecuado
  useEffect(() => {
    // Si no hay `visiblePages` o son menores a 5, inicializarlos
    if (!visiblePages || visiblePages.length < 5) {
      const pages = [];
      for (let i = 1; i <= Math.min(5, totalPages); i++) {
        pages.push(i);
      }
      setVisiblePages(pages);
    }
  }, [visiblePages, setVisiblePages, totalPages]);

  // Manejo de la página siguiente
  const handleNextPages = () => {
    if (visiblePages[4] < totalPages) {
      const newPages = visiblePages.map((page) => page + 1);
      setVisiblePages(newPages);
    }
  };

  // Manejo de la página anterior
  const handlePrevPages = () => {
    if (visiblePages[0] > 1) {
      const newPages = visiblePages.map((page) => page - 1);
      setVisiblePages(newPages);
    }
  };

  // Verificar que el `currentPage` esté dentro del rango de las páginas visibles
  useEffect(() => {
    if (currentPage < visiblePages[0]) {
      setCurrentPage(visiblePages[0]);
    } else if (currentPage > visiblePages[4]) {
      setCurrentPage(visiblePages[4]);
    }
  }, [currentPage, visiblePages, setCurrentPage]);

  return (
    <div className="pagination">
      <button
        onClick={handlePrevPages}
        disabled={visiblePages[0] === 1}
      >
        &lt;
      </button>

      {visiblePages.map((page) => (
        <button
          key={page}
          onClick={() => setCurrentPage(page)}
          className={page === currentPage ? 'active' : ''}
        >
          {page}
        </button>
      ))}

      <button
        onClick={handleNextPages}
        disabled={visiblePages[4] >= totalPages}
      >
        &gt;
      </button>
    </div>
  );
}

export default Pagination;
