import React, { useState, useEffect } from "react";
import { useFavorites } from "../components/FavoritesContext";
import { useCart } from "../components/CartContext";
import { useAuth0 } from "@auth0/auth0-react";
import { useLocation } from "react-router-dom";
import RecipeList from "./RecipeList";
import Footer from "../components/Footer";
import "./Recipes.css";

function SearchResults() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const location = useLocation();

  const searchParams = new URLSearchParams(location.search);
  const searchTerm = searchParams.get("query") || "";

  const API_KEY = "540464a4610b4e4c9488d105323ad0af";
  const URL = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&query=${searchTerm}&number=100`;

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.results);
        } else {
          throw new Error(data.message || "No recipes found.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (searchTerm) {
      fetchRecipes();
    } else {
      setLoading(false);
      setError("Please enter a search term.");
    }
  }, [searchTerm]);

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      return loginWithRedirect();
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      return loginWithRedirect();
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  if (loading) {
    return <div className="loading-container">Searching recipes...</div>;
  }

  if (error) {
    return <div className="error-container">{`Error: ${error}`}</div>;
  }

  return (
    <section className="container-recipes">
      <div className="main-content">
        <h2 className="search-results-title">
          Search Results for "{searchTerm}"
        </h2>
        {recipes.length > 0 ? (
          <RecipeList
            recipes={recipes}
            addToCartHandler={addToCartHandler}
            toggleSaveRecipe={toggleSaveRecipe}
            favorites={favorites}
            cart={cart}
          />
        ) : (
          <div className="no-results">No recipes found.</div>
        )}
      </div>
      <Footer />
    </section>
  );
}

export default SearchResults;
