import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Home.css";

function HomePopulars() {

    const [recipes, setRecipes] = useState([]); //Estado que guarda las recetas
    const [loading, setLoading] = useState(true); //Maneja el loading
    const [error, setError] = useState(nuell); //Maneja los errores
    const [currentIndex, setCurrentIndex] = useState(0);
    const [savedRecipes, setSavedIndex] = useState([]); //Estado para guardar las recetas

    const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
    const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=10`;

    const navigate = useNavigate(); //Hook para navegar entre rutas

    useEffect(() => {
        const fetchRecipes = async () => {
            try {
                const response = await fetch(URL);
                const data = await response.json();

                if (response.ok) {
                    setRecipes(data.recipes);
                } else {
                    throw new Error(data.message || "The recipes could not be loaded.");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };
        fetchRecipes();
    }, []);

    //Para poder utilizar el carrusel hacia adelante
    const nextRecipes = () => {
        if (currentIndex + 4 < recipes.length) {
            setCurrentIndex((prevIndex) => prevIndex + 4);
        }
    };

    //Para poder utilizar el carrusel hacia atrás
    const prevRecipes = () => {
        if (currentIndex > 0) {
            setCurrentIndex((prevIndex) => prevIndex - 4);
        }
    };

    //Agregar o eliminar una receta de Favoritos
    const toggleSaveRecipes = (id) => {
        setSavedRecipes((prev) =>
            prev.includes(id)
                ? prev.filter((recipeId)=>recipeId !==id)
            :[...prev,id]);
    }

    if (loading) {
        return <p>Loading Recipes...</p>;
    }

    if (error) {
        return <p>{`Erro: ${error}`}</p>;
    }

    return (
        <section className="container-populars">
      {/* Contenedor principal del carrusel */}
      <div className="home-container-populars">
        <h1 className="home-populars-title">Recetas Destacadas</h1>

        {/* Carrusel de recetas */}
        <div className="carousel-multiple">
          {/* Botón para desplazarse hacia atrás */}
          <button className="carousel-btn prev" onClick={prevRecipes} disabled={currentIndex === 0}>
            <i className="bx bx-chevron-left"></i>
          </button>

          {/* Área principal donde se muestran las recetas */}
          <div className="carousel-track">
            {recipes.slice(currentIndex, currentIndex + 4).map((recipe) => (
              <div
                key={recipe.id}
                className="carousel-item"
                onClick={() => navigate(`/recipe/${recipe.id}`)} // Navegar al detalle de la receta
              >
                {/* Imagen de la receta */}
                <img
                  src={recipe.image}
                  alt={recipe.title}
                  className="carousel-image"
                />

                {/* Información de la receta */}
                <div className="carousel-info">
                  <h3 className="carousel-title">{recipe.title}</h3>
                  
                  {/* Metadatos de la receta */}
                  <div className="carousel-meta">
                    {/* Tiempo de preparación */}
                    <span>
                      <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
                    </span>

                    {/* Botón para guardar o quitar de favoritos */}
                    <button
                      className="save-btn"
                      onClick={(e) => {
                        e.stopPropagation(); // Evitar que el clic navegue a la receta
                        toggleSaveRecipe(recipe.id);
                      }}
                    >
                      {savedRecipes.includes(recipe.id) ? (
                        <i className="bx bxs-heart"></i> // Icono de corazón lleno
                      ) : (
                        <i className="bx bx-heart"></i> // Icono de corazón vacío
                      )}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Botón para desplazarse hacia adelante */}
          <button
            className="carousel-btn next"
            onClick={nextRecipes}
            disabled={currentIndex + 4 >= recipes.length}
          >
            <i className="bx bx-chevron-right"></i>
          </button>
        </div>
      </div>
    </section>

    );
}

export default HomePopulars;
