import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Importamos useNavigate
import { useFavorites } from "./FavoritesContext"; // Cambiado a FavoritesContext
import { useCart } from "../components/CartContext"; // Contexto de carrito
import "./Favorites.css";

const Favorites = () => {
    const { favorites, removeFavorite } = useFavorites();
    const { addToCart } = useCart();
    const [ingredients, setIngredients] = useState({});
    const [expand, setExpand] = useState({}); // Para manejar la expansión de secciones
    const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
    const navigate = useNavigate(); // Inicializamos useNavigate para la navegación

    // Función para obtener ingredientes de una receta favorita
    const fetchIngredients = async (recipeId) => {
        const URL = `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=${API_KEY}`;
        try {
            const response = await fetch(URL);
            const data = await response.json();
            const recipeIngredients = data.extendedIngredients || [];
            setIngredients((prev) => ({
                ...prev,
                [recipeId]: recipeIngredients,
            }));
        } catch (error) {
            console.error("Error fetching ingredients:", error);
        }
    };

    useEffect(() => {
        favorites.forEach((recipe) => {
            if (!ingredients[recipe.id]) {
                fetchIngredients(recipe.id);
            }
        });
    }, [favorites]);

    // Función para manejar la expansión de la sección de ingredientes
    const toggleExpand = (recipeId) => {
        setExpand((prev) => ({
            ...prev,
            [recipeId]: !prev[recipeId],
        }));
    };

    // Función para navegar a la página de detalles de la receta
    const goToRecipeDetails = (recipeId) => {
        navigate(`/recipe/${recipeId}`); // Redirige a la página de detalles de la receta
    };

    return (
        <div>
            <h1>Your Favorites</h1>
            {favorites.length === 0 ? (
                <p>You have no favorite recipes yet!</p>
            ) : (
                <div className="favorites-container">
                    {favorites.map((recipe) => (
                        <div key={recipe.id} className="favorite-item">
                            <img
                                src={recipe.image}
                                alt={recipe.title}
                                className="favorite-image"
                                onClick={() => goToRecipeDetails(recipe.id)} // Navegar al hacer clic en la imagen
                            />
                            <div
                                className={`favorite-info ${expand[recipe.id] ? "expanded" : ""}`}
                                onClick={() => toggleExpand(recipe.id)} // Navegar al hacer clic en el título
                            >
                                <h3>{recipe.title}</h3> {/* Navegar al hacer clic en el título */}
                                <h4>
                                    <i className="bx bx-time-five"></i>
                                    {recipe.readyInMinutes} minutes
                                </h4>
                                <p>
                                    {recipe.summary
                                        ? recipe.summary.replace(/(<([^>]+)>)/gi, "")
                                        : "No description available."}
                                </p>
                                {expand[recipe.id] && ingredients[recipe.id] && (
                                    <div className="ingredients-list">
                                        <h5>Ingredients:</h5>
                                        <ul>
                                            {ingredients[recipe.id].map((ingredient, index) => (
                                                <li key={index}>{ingredient.originalString}</li>
                                            ))}
                                        </ul>
                                    </div>
                                )}
                            </div>
                            <div className="favorite-buttons">
                                <button onClick={() => removeFavorite(recipe.id)}>
                                    <i className="bx bx-trash"></i>
                                </button>
                                <button onClick={() => addToCart(recipe)}>
                                    <i className="bx bx-plus"></i>
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Favorites;
