import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useCart } from "../components/CartContext";
import { useAuth0 } from "@auth0/auth0-react";
import DietaryIcon from "./DietaryIcon"; // Importamos el nuevo componente.
import "./HomeDetails.css";

function HomeDetails() {
  const { recipeId } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await fetch(
          `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=d0fba68ef5204602ac929844f28b7d5f`
        );
        const data = await response.json();
        if (response.ok) {
          setRecipe(data);
        } else {
          throw new Error(data.message || "Recipe not found.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [recipeId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <section className="recipe-detail">
      <div className="recipe-detail-container">
        <button className="back-btn" onClick={() => navigate("/")}>
          Back
        </button>
        <div className="recipe-detail-header">
          <img src={recipe.image} alt={recipe.title} className="recipe-detail-image" />
          <h1>{recipe.title}</h1>
          <p>{recipe.readyInMinutes} minutes</p>
        </div>

        <div className="dietary-restrictions">
          <h3>Dietary Restrictions:</h3>
          <div className="dietary-icons-container">
            {recipe.diets && recipe.diets.length > 0 ? (
              recipe.diets.map((diet, index) => <DietaryIcon key={index} diet={diet} />)
            ) : (
              <p>No dietary restrictions found for this recipe.</p>
            )}
          </div>
        </div>
      </div>
    </section>
  );
}

export default HomeDetails;
