import React from "react";
import "./HomeDetails.css";

function HomeDetails({ recipe, onClose }) {
    if (!recipe) return null;

    return (
        <div className="recipe-details-overlay">
            <div className="recipe-details">
                <button className="close-details-btn" onClick={onClose}>
                    Close
                </button>
                <h2>{recipe.title}</h2>
                <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="details-image"
                />
                <p dangerouslySetInnerHTML={{ __html: recipe.summary }}></p>
                <ul>
                    <li>{recipe.vegan ? "Vegan" : "Not Vegan"}</li>
                    <li>{recipe.glutenFree ? "Gluten Free" : "Contains Gluten"}</li>
                    <li>{recipe.vegetarian ? "Vegetarian" : "Not Vegetarian"}</li>
                </ul>
            </div>
        </div>
    );
}

export default HomeDetails;
