import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useFavorites } from "./FavoritesContext"; // Importar el contexto
import "./Home.css";

function HomePopulars() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const { favorites, toggleFavorite } = useFavorites(); // Obtener favoritos y toggle

  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=10`;
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.recipes);
        } else {
          throw new Error(data.message || "Error al cargar las recetas");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const nextRecipes = () => {
    if (currentIndex + 4 < recipes.length) {
      setCurrentIndex((prevIndex) => prevIndex + 4);
    }
  };

  const prevRecipes = () => {
    if (currentIndex > 0) {
      setCurrentIndex((prevIndex) => prevIndex - 4);
    }
  };

  if (loading) {
    return <p>Cargando recetas...</p>;
  }

  if (error) {
    return <p>{`Error: ${error}`}</p>;
  }

  return (
    <section className="container-populars">
      <div className="home-container-populars">
        <h1 className="home-populars-title">Recetas Destacadas</h1>

        <div className="carousel-multiple">
          <button className="carousel-btn prev" onClick={prevRecipes} disabled={currentIndex === 0}>
            <i className="bx bx-chevron-left"></i>
          </button>

          <div className="carousel-track">
            {recipes.slice(currentIndex, currentIndex + 4).map((recipe) => (
              <div
                key={recipe.id}
                className="carousel-item"
                onClick={() => navigate(`/recipe/${recipe.id}`)}
              >
                <img src={recipe.image} alt={recipe.title} className="carousel-image" />

                <div className="carousel-info">
                  <h3 className="carousel-title">{recipe.title}</h3>
                  <div className="carousel-meta">
                    <span>
                      <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
                    </span>
                    <button
                      className="save-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleFavorite(recipe); // Usar toggleFavorite
                      }}
                    >
                      {favorites.some((fav) => fav.id === recipe.id) ? (
                        <i className="bx bxs-heart"></i>
                      ) : (
                        <i className="bx bx-heart"></i>
                      )}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button
            className="carousel-btn next"
            onClick={nextRecipes}
            disabled={currentIndex + 4 >= recipes.length}
          >
            <i className="bx bx-chevron-right"></i>
          </button>
        </div>
      </div>
    </section>
  );
}

export default HomePopulars;
