import React, { useState, useEffect } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useCart } from "../components/CartContext";
import { useAuth0 } from "@auth0/auth0-react";
import DietaryIcon from "../components/DietaryIcon";
import SimilarRecipes from "./SimilarRecipes";  // Import the new component
import "boxicons/css/boxicons.min.css";
import "./HomeDetails.css";

function HomeDetails() {
    const { recipeId } = useParams();
    const [recipe, setRecipe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const { favorites, addFavorite, removeFavorite } = useFavorites();
    const { cart, addToCart, removeFromCart } = useCart();
    const { isAuthenticated, loginWithRedirect } = useAuth0();

    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        const fetchRecipe = async () => {
            try {
                const response = await fetch(
                    `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=d0fba68ef5204602ac929844f28b7d5f`
                );
                const data = await response.json();

                if (response.ok) {
                    setRecipe(data);
                } else {
                    throw new Error(data.message || "Recipe not found.");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchRecipe();
    }, [recipeId]);

    const handleBackButtonClick = () => {
        const fromPage = location.state?.fromFavorites;
        const fromCart = location.state?.fromCart;
        const fromRecipeList = location.state?.fromRecipeList;
        const fromSearchResults = location.state?.fromSearchResults;

        if (fromPage) {
            navigate("/favorites");
        } else if (fromCart) {
            navigate("/cart");
        } else if (fromRecipeList) {
            navigate("/recipes");
        } else if (fromSearchResults) {
            navigate("/recipes");
        } else {
            navigate("/");
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <section className="recipe-detail">
            <div className="recipe-detail-container">
                <button className="back-btn" onClick={handleBackButtonClick}>
                    <p><i className="bx bx-arrow-back"></i> BACK</p>
                </button>
                <div className="recipe-detail-header">
                    <img src={recipe.image} alt={recipe.title} className="recipe-detail-image" />
                    <div className="recipe-detail-info">
                        <h1>{recipe.title}</h1>
                        <p><i className="bx bx-time-five"></i> {recipe.readyInMinutes} minutes</p>
                        {/* Recipe Description */}
                        <div className="recipe-description">
                            <h3>Description</h3>
                            <p>{recipe.summary}</p>
                        </div>

                        {/* Display Similar Recipes */}
                        <SimilarRecipes recipeId={recipe.id} ingredients={recipe.extendedIngredients.map(ing => ing.name)} />
                    </div>
                </div>
            </div>
        </section>
    );
}

export default HomeDetails;
