import React from 'react';

function Pagination({ currentPage, setCurrentPage, visiblePages, setVisiblePages, recipes, recipesPerPage }) {
  const handleNextPages = () => {
    if (visiblePages[4] < Math.ceil(recipes.length / recipesPerPage)) {
      setVisiblePages(visiblePages.map(page => page + 1));
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPages = () => {
    if (visiblePages[0] > 1) {
      setVisiblePages(visiblePages.map(page => page - 1));
      setCurrentPage(currentPage - 1);
    }
  };

  const handlePageClick = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="pagination-container">
      <button onClick={handlePreviousPages} disabled={visiblePages[0] === 1}>
        Previous
      </button>

      {visiblePages.map((page, index) => (
        <button
          key={index}
          className={page === currentPage ? 'active' : ''}
          onClick={() => handlePageClick(page)}
        >
          {page}
        </button>
      ))}

      <button onClick={handleNextPages} disabled={visiblePages[4] >= Math.ceil(recipes.length / recipesPerPage)}>
        Next
      </button>
    </div>
  );
}

export default Pagination;
