import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useAuth0 } from "@auth0/auth0-react";
import { useCart } from "../components/CartContext";
import "./Recipes.css";

function Recipes() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showLoginMessage, setShowLoginMessage] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [visiblePages, setVisiblePages] = useState([1, 2, 3, 4, 5]);
  const recipesPerPage = 6;

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const URL = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&number=100`;

  const navigate = useNavigate();

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.results);
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  const indexOfLastRecipe = currentPage * recipesPerPage;
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;
  const currentRecipes = recipes.slice(indexOfFirstRecipe, indexOfLastRecipe);

  const handleNextPages = () => {
    if (visiblePages[4] < Math.ceil(recipes.length / recipesPerPage)) {
      setVisiblePages(visiblePages.map((page) => page + 1));
    }
  };

  const handlePrevPages = () => {
    if (visiblePages[0] > 1) {
      setVisiblePages(visiblePages.map((page) => page - 1));
    }
  };

  const fastestRecipes = recipes
    .filter((recipe) => recipe.readyInMinutes !== undefined)
    .sort((a, b) => a.readyInMinutes - b.readyInMinutes)
    .slice(0, 5);

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading recipes...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{`Error: ${error}`}</p>
      </div>
    );
  }

  return (
    <section className="container-recipes">
      <div className="recipes-container">
        <div className="main-content">
          <div className="recipes-grid">
            {currentRecipes.map((recipe) => (
              <div
                key={recipe.id}
                className="recipe-card"
                onClick={() => navigate(`/recipe/${recipe.id}`)}
              >
                <div className="recipe-image1">
                  <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="recipe-image"
                  />
                </div>
                <div className="recipe-info">
                  <h3 className="recipe-title">{recipe.title}</h3>

                  <div className="recipe-meta">
                    <span>
                      <i className="bx bx-time-five"></i>{" "}
                      {recipe.readyInMinutes !== undefined
                        ? recipe.readyInMinutes
                        : "N/A"}{" "}
                      min
                    </span>

                    <button
                      className={`save-btn ${favorites.some(
                        (fav) => fav.id === recipe.id
                      )
                        ? "saved"
                        : ""
                        }`}
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleSaveRecipe(recipe);
                      }}
                      aria-label={
                        favorites.some((fav) => fav.id === recipe.id)
                          ? "Remove from saved recipes"
                          : "Save recipe"
                      }
                    >
                      <i
                        className={
                          favorites.some((fav) => fav.id === recipe.id)
                            ? "bx bxs-heart"
                            : "bx bx-heart"
                        }
                      ></i>
                    </button>

                    <button
                      className="details-button"
                      onClick={(e) => {
                        addToCartHandler(recipe, e);
                      }}
                      aria-label={
                        cart.some((item) => item.id === recipe.id)
                          ? "Remove from cart"
                          : "Add to cart"
                      }
                    >
                      <i
                        className={
                          cart.some((item) => item.id === recipe.id)
                            ? "bx bxs-plus"
                            : "bx bx-plus"
                        }
                      ></i>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="pagination">
            <button onClick={handlePrevPages} disabled={visiblePages[0] === 1}>
              &lt;
            </button>
            {visiblePages.map((page) => (
              <button
                key={page}
                onClick={() => setCurrentPage(page)}
                className={page === currentPage ? "active" : ""}
              >
                {page}
              </button>
            ))}
            <button
              onClick={handleNextPages}
              disabled={
                visiblePages[4] >= Math.ceil(recipes.length / recipesPerPage)
              }
            >
              &gt;
            </button>
          </div>
        </div>

        <aside className="fast-recipes">
          <h3>Fastest Recipes</h3>
          <ul>
            {fastestRecipes.map((recipe) => (
              <li
                key={recipe.id}
                onClick={() => navigate(`/recipe/${recipe.id}`)}
              >
                <img src={recipe.image} alt={recipe.title} />
                <span>{recipe.title}</span>
                <span>
                  <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
                </span>
              </li>
            ))}
          </ul>
        </aside>
      </div>
    </section>
  );
}

export default Recipes;
