import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext"; // Importar el contexto
import { useAuth0 } from "@auth0/auth0-react"; // Importar el hook useAuth0
import { useCart } from "../components/CartContext"; // Importar el hook del carrito
import Filters from "../components-recipes/Filters"; // Importar componente de filtros
import Pagination from "../components-recipes/Pagination"; // Importar componente de paginación
import "./Recipes.css";

function RecipeList() {
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showLoginMessage, setShowLoginMessage] = useState(false);
  const [currentPage, setCurrentPage] = useState(1); // Estado de la página actual
  const [visiblePages, setVisiblePages] = useState([1, 2, 3, 4, 5]); // Páginas visibles para la paginación
  const recipesPerPage = 20; // Número de recetas por página

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=100`;

  const navigate = useNavigate();

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  const handleApplyFilters = (filters) => {
    let updatedRecipes = [...recipes];

    if (filters.cuisine.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        filters.cuisine.includes(recipe.cuisine)
      );
    }

    if (filters.mealType.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        filters.mealType.includes(recipe.mealType)
      );
    }

    if (filters.dietary.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        filters.dietary.some((diet) => recipe.dietary.includes(diet))
      );
    }

    if (filters.cookingTime.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        filters.cookingTime.includes(recipe.readyInMinutes)
      );
    }

    if (filters.difficulty.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        filters.difficulty.includes(recipe.difficulty)
      );
    }

    setFilteredRecipes(updatedRecipes);
    setCurrentPage(1); // Reset to first page when filters are applied
  };

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.recipes);
          setFilteredRecipes(data.recipes); // Inicializamos las recetas filtradas con todas las recetas
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  const indexOfLastRecipe = currentPage * recipesPerPage;
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;
  const currentRecipes = filteredRecipes.slice(indexOfFirstRecipe, indexOfLastRecipe);

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading recipes...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{`Error: ${error}`}</p>
      </div>
    );
  }

  return (
    <section className="container-populars">
      <div className="home-container-populars">
        {showLoginMessage && (
          <div className="login-message">
            <p>You need to log in to save recipes or add them to your cart.</p>
            <div className="button-container">
              <button className="login-button" onClick={loginWithRedirect}>
                Log In
              </button>
              <button
                className="close-button"
                onClick={() => setShowLoginMessage(false)}
              >
                Close
              </button>
            </div>
          </div>
        )}

        <Filters onApplyFilters={handleApplyFilters} />

        <div className="recipe-list">
          {currentRecipes.map((recipe) => (
            <div
              key={recipe.id}
              className="recipe-card"
              onClick={() =>
                navigate(`/recipe/${recipe.id}`, { state: { fromRecipes: true } })
              }
            >
              <div className="recipe-image1">
                <img src={recipe.image} alt={recipe.title} className="recipe-image" />
              </div>
              <div className="recipe-info">
                <h3 className="carousel-title">{recipe.title}</h3>
                <div className="carousel-meta">
                  <span>
                    <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
                  </span>
                  <button
                    className={`save-btn ${favorites.some((fav) => fav.id === recipe.id) ? "saved" : ""}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleSaveRecipe(recipe);
                    }}
                    aria-label={
                      favorites.some((fav) => fav.id === recipe.id)
                        ? "Remove from saved recipes"
                        : "Save recipe"
                    }
                  >
                    <i
                      className={favorites.some((fav) => fav.id === recipe.id)
                        ? "bx bxs-heart"
                        : "bx bx-heart"}
                    ></i>
                  </button>

                  {/* Botón para carrito */}
                  <button
                    className="details-button"
                    onClick={(e) => {
                      addToCartHandler(recipe, e); // Llama a la función para agregar/eliminar del carrito
                    }}
                    aria-label={
                      cart.some((item) => item.id === recipe.id)
                        ? "Remove from cart"
                        : "Add to cart"
                    }
                  >
                    <i
                      className={cart.some((item) => item.id === recipe.id)
                        ? "bx bxs-plus"
                        : "bx bx-plus"}
                    ></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Paginación */}
        <Pagination
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          visiblePages={visiblePages}
          setVisiblePages={setVisiblePages}
          recipes={filteredRecipes}
          recipesPerPage={recipesPerPage}
        />
      </div>
    </section>
  );
}

export default RecipeList;
