import React, { useState, useEffect } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useCart } from "../components/CartContext";
import { useAuth0 } from "@auth0/auth0-react";
import DietaryIcon from "../components/DietaryIcon";
import "boxicons/css/boxicons.min.css";
import "./HomeDetails.css";

function HomeDetails() {
    const { recipeId } = useParams();
    const [recipe, setRecipe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const { favorites, addFavorite, removeFavorite } = useFavorites();
    const { cart, addToCart, removeFromCart } = useCart();
    const { isAuthenticated, loginWithRedirect } = useAuth0();

    const navigate = useNavigate();
    const location = useLocation();  // Hook para obtener la ubicación actual

    const [expandedSections, setExpandedSections] = useState({
        description: true,
        ingredients: false,
        nutritionalInfo: false,
        additionalInfo: false,
        dietaryRestrictions: false,
    });

    const toggleSection = (section) => {
        setExpandedSections((prev) => ({
            ...prev,
            [section]: !prev[section],
        }));
    };

    useEffect(() => {
        const fetchRecipe = async () => {
            try {
                const response = await fetch(
                    `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=d0fba68ef5204602ac929844f28b7d5f`
                );
                const data = await response.json();

                if (response.ok) {
                    setRecipe(data);
                } else {
                    throw new Error(data.message || "Recipe not found.");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchRecipe();
    }, [recipeId]);

    const toggleSaveRecipe = () => {
        if (!isAuthenticated) {
            loginWithRedirect();
            return;
        }

        if (favorites.some((fav) => fav.id === recipe.id)) {
            removeFavorite(recipe.id);
        } else {
            addFavorite(recipe);
        }
    };

    const addToCartHandler = (e) => {
        e.stopPropagation();
        if (!isAuthenticated) {
            loginWithRedirect();
            return;
        }

        if (cart.some((item) => item.id === recipe.id)) {
            removeFromCart(recipe.id);
        } else {
            addToCart(recipe);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    const nutritionalInfo =
        recipe.nutrition && recipe.nutrition.nutrients
            ? recipe.nutrition.nutrients
            : [];

    const handleBackButtonClick = () => {
        if (location.state?.fromRecipes) {
            navigate("/recipes");
        } else {
            navigate("/"); // Página de inicio por defecto
        }
    };

    return (
        <div className="recipe-details-container">
            <button
                className="back-button"
                onClick={handleBackButtonClick}
            >
                <i className="bx bx-arrow-back"></i> Back
            </button>
            <div className="recipe-details">
                <h1>{recipe.title}</h1>
                <img src={recipe.image} alt={recipe.title} />
                <div className="recipe-info">
                    <div className="details-toggle">
                        <button onClick={() => toggleSection("description")}>
                            {expandedSections.description
                                ? "Hide Description"
                                : "Show Description"}
                        </button>
                    </div>
                    {expandedSections.description && (
                        <p>{recipe.instructions}</p>
                    )}
                </div>
                {/* Otras secciones del detalle como ingredientes, información nutricional, etc. */}
            </div>
        </div>
    );
}

export default HomeDetails;
