import React, { useState, useEffect } from "react";
import L from "leaflet";
import 'leaflet/dist/leaflet.css';
import "./Restaurants.css";

//const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
const API_KEY = "540464a4610b4e4c9488d105323ad0af"; // Usar esta cuando nos quedemos sin puntos en la otra

function Restaurants() {
  const [lat, setLat] = useState("");
  const [lng, setLng] = useState("");
  const [cuisine, setCuisine] = useState("");
  const [distance, setDistance] = useState(5); // Default distance in miles
  const [budget, setBudget] = useState("");
  const [minRating, setMinRating] = useState("");
  const [restaurants, setRestaurants] = useState([]);
  const [page, setPage] = useState(0);
  const [map, setMap] = useState(null);

  //pide la localizaciÃ³n del usuairo
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLat(position.coords.latitude);
          setLng(position.coords.longitude);
        },
        (error) => console.error("Error getting location", error),
        { enableHighAccuracy: true }
      );
    }
  }, []);

  //inicializa el mapa
  useEffect(() => {
    if (lat && lng && !map) {
      const newMap = L.map("map").setView([lat, lng], 13);
      L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
        attribution: "&copy; OpenStreetMap contributors",
      }).addTo(newMap);
      setMap(newMap);
    }
  }, [lat, lng, map]);

  //fetch de restaurantes de spoonacular
  const fetchRestaurants = async () => {
    try {
      const response = await fetch(
        `https://api.spoonacular.com/food/restaurants/search?lat=${lat}&lng=${lng}&cuisine=${cuisine}&distance=${distance}&budget=${budget}&min-rating=${minRating}&page=${page}&apiKey=${API_KEY}`
      );
      const data = await response.json();
      setRestaurants(data.restaurants || []);
      updateMap(data.restaurants || []);
    } catch (error) {
      console.error("Error fetching restaurants:", error);
    }
  };

  //actualiza los markers del mapa
  const updateMap = (restaurants) => {
    if (map) {
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker) {
          map.removeLayer(layer);
        }
      });

      restaurants.forEach((restaurant) => {
        L.marker([restaurant.address.latitude, restaurant.address.longitude])
          .addTo(map)
          .bindPopup(`<b>${restaurant.name}</b><br>${restaurant.address.street_addr}`);
      });
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchRestaurants();
  };

  const displayedRestaurants = restaurants.slice(0, 4);
  
  return (
    <div>
      <h1>Find the Best Restaurants</h1>
      <form onSubmit={handleSearch}>
        <input
          type="number"
          placeholder="Latitude"
          value={lat}
          onChange={(e) => setLat(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Longitude"
          value={lng}
          onChange={(e) => setLng(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Cuisine Type"
          value={cuisine}
          onChange={(e) => setCuisine(e.target.value)}
        />
        <input
          type="number"
          placeholder="Distance (miles)"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
        />
        <input
          type="number"
          placeholder="Budget ($)"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />
        <input
          type="number"
          step="0.1"
          placeholder="Min Rating (0-5)"
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      <div id="map" style={{ height: "400px", width: "100%", marginTop: "20px" }}></div>

      <div>
        <h2>Results</h2>
        {displayedRestaurants.length > 0 ? (
          displayedRestaurants.map((restaurant, index) => (
            <div key={index}>
              <h3>{restaurant.name}</h3>
              <p>ð {restaurant.address.street_addr}, {restaurant.address.city}</p>
              <p>â­ {restaurant.weighted_rating_value.toFixed(1)} ({restaurant.aggregated_rating_count} reviews)</p>
              <p>ð° Price Level: {"$".repeat(restaurant.dollar_signs)}</p>
              <p>ð Open Now: {restaurant.is_open ? "Yes" : "No"}</p>
              <img src={restaurant.food_photos?.[0] || restaurant.logo_photos?.[0]} alt={restaurant.name} width="100" />
            </div>
          ))
        ) : (
          <p>No results found.</p>
        )}
      </div>

      {restaurants.length > 4 && (
        <div>
          <button disabled={page === 0} onClick={() => setPage(page - 1)}>
            Previous
          </button>
          <span> Page {page + 1} </span>
          <button onClick={() => setPage(page + 1)}>Next</button>
        </div>
      )}
    </div>
  ) 
}

export default Restaurants;
