import React from "react";
import "./HomeDetails.css";

function HomeDetails({ recipe, onClose, toggleFavorite, addToCartHandler, isFavorite, isInCart }) {
    if (!recipe) return null;

    return (
        <div className="recipe-details-overlay">
            <div className="recipe-details">
                <button className="close-details-btn" onClick={onClose}>
                    &times;
                </button>
                <h2 className="details-title">{recipe.title}</h2>
                <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="details-image"
                />
                <p className="details-description" dangerouslySetInnerHTML={{ __html: recipe.summary }}></p>
                <ul className="details-list">
                    <li>{recipe.vegan ? "Vegan" : "Not Vegan"}</li>
                    <li>{recipe.glutenFree ? "Gluten Free" : "Contains Gluten"}</li>
                    <li>{recipe.vegetarian ? "Vegetarian" : "Not Vegetarian"}</li>
                </ul>
                <div className="details-buttons">
                    <button
                        className={`details-favorite-btn ${isFavorite ? "favorite" : ""}`}
                        onClick={() => toggleFavorite(recipe)}
                    >
                        {isFavorite ? "Remove from Favorites" : "Add to Favorites"}
                    </button>
                    <button
                        className={`details-cart-btn ${isInCart ? "in-cart" : ""}`}
                        onClick={() => addToCartHandler(recipe)}
                    >
                        {isInCart ? "Remove from Cart" : "Add to Cart"}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default HomeDetails;
