import React, { useState, useEffect } from "react";
import { useCart } from "../components/CartContext";
import { useFavorites } from "../components/FavoritesContext";
import DietaryIcon from "../components/DietaryIcon"; // Importamos el nuevo componente.
import "./Cart.css";

const Cart = () => {
  const { cart, removeFromCart } = useCart();
  const { addFavorite } = useFavorites();
  const [ingredients, setIngredients] = useState({});
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul className="cart-list">
          {cart.map((recipe) => (
            <li key={recipe.id} className="cart-item">
              <div className="recipe-header">
                <img src={recipe.image} alt={recipe.title} className="recipe-image" />
                <div className="recipe-details">
                  <h3>{recipe.title}</h3>
                  <div className="dietary-icons-container">
                    {recipe.diets.map((diet, index) => (
                      <DietaryIcon key={index} diet={diet} />
                    ))}
                  </div>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
