import React, { useState, useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useLocation, Link } from "react-router-dom";
import "./SearchResults.css"; // Asegúrate de que contiene el estilo proporcionado.

function SearchResults() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const location = useLocation();

  const searchParams = new URLSearchParams(location.search);
  const searchTerm = searchParams.get("query") || "";

  const API_KEY = "540464a4610b4e4c9488d105323ad0af";
  const URL = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&query=${searchTerm}&number=12`;

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data.results);
        } else {
          throw new Error(data.message || "No recipes found.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (searchTerm) {
      fetchRecipes();
    } else {
      setLoading(false);
      setError("Please enter a search term.");
    }
  }, [searchTerm]);

  if (loading) {
    return <div className="loading-container">Searching recipes...</div>;
  }

  if (error) {
    return <div className="error-container">Error: {error}</div>;
  }

  return (
    <section className="container-populars1">
      <div className="home-populars-texts1">
        <h2 className="home-populars-title1">Search Results</h2>
        <p className="home-populars-subtitle1">
          Results for "{searchTerm}" ({recipes.length} found)
        </p>
      </div>
      <div className="recipe-list1">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card1">
            <div className="recipe-image1">
              <img
                src={recipe.image}
                alt={recipe.title}
                className="recipe-image1"
              />
            </div>
            <div className="recipe-info1">
              <h3>{recipe.title}</h3>
              <div className="carousel-meta1">
                <button
                  className="save-btn1"
                  onClick={() =>
                    isAuthenticated ? alert("Save functionality coming soon!") : loginWithRedirect()
                  }
                >
                  <i className="bx bx-heart"></i>
                </button>
                <Link to={`/recipe/${recipe.id}`}>
                  <button className="details-button1">Details</button>
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default SearchResults;
