import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./HomeDetails.css";

function HomeDetails() {
    const { id } = useParams();
    const [recipe, setRecipe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
    const URL = `https://api.spoonacular.com/recipes/${id}/information?apiKey=${API_KEY}`;

    useEffect(() => {
        const fetchRecipe = async () => {
            try {
                const response = await fetch(URL);
                const data = await response.json();

                if (response.ok) {
                    setRecipe(data);
                } else {
                    throw new Error(data.message || "Recipe not found.");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchRecipe();
    }, [id]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{`Error: ${error}`}</div>;
    }

    return (
        <div className="recipe-details-container">
            <h2>{recipe.title}</h2>
            <img src={recipe.image} alt={recipe.title} />
            <div className="recipe-info">
                <h3>Summary:</h3>
                <p dangerouslySetInnerHTML={{ __html: recipe.summary }}></p>
                <h3>Details:</h3>
                <ul>
                    <li>{recipe.vegan ? "Vegan" : "Not Vegan"}</li>
                    <li>{recipe.glutenFree ? "Gluten Free" : "Contains Gluten"}</li>
                    <li>{recipe.vegetarian ? "Vegetarian" : "Not Vegetarian"}</li>
                </ul>
                <div className="buttons-container">
                    <button className="save-btn">Add to Favorites</button>
                    <button className="details-button">Add to Cart</button>
                </div>
            </div>
        </div>
    );
}

export default HomeDetails;
