import React, { createContext, useContext, useState } from "react";

// Crear el contexto
const FavoritesContext = createContext();

// Proveedor del contexto
export const FavoritesProvider = ({ children }) => {
  const [favorites, setFavorites] = useState([]); // Estado para favoritos

  // Función para agregar o eliminar recetas
  const toggleFavorite = (recipe) => {
    setFavorites((prev) => {
      const exists = prev.find((item) => item.id === recipe.id);
      if (exists) {
        // Si ya está en favoritos, eliminarla
        return prev.filter((item) => item.id !== recipe.id);
      } else {
        // Si no está, agregarla
        return [...prev, recipe];
      }
    });
  };

  return (
    <FavoritesContext.Provider value={{ favorites, toggleFavorite }}>
      {children}
    </FavoritesContext.Provider>
  );
};

// Hook para usar el contexto
export const useFavorites = () => useContext(FavoritesContext);
