import React, { useState, useEffect } from "react";
import { useCart } from "../components/CartContext";

const Cart = () => {
    const { cart, removeFromCart } = useCart();
    const [ingredients, setIngredients] = useState({});

    useEffect(() => {
        const fetchIngredients = async (recipeId) => {
            const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
            const URL = `https://api.spoonacular.com/recipes/${recipeId}/ingredientWidget.json?apiKey=${API_KEY}`;

            try {
                const response = await fetch(URL);
                const data = await response.json();
                setIngredients((prev) => ({ ...prev, [recipeId]: data.ingredients }));
            } catch (error) {
                console.error("Error fetching ingredients:", error);
            }
        };

        cart.forEach((recipe) => {
            if (!ingredients[recipe.id]) {
                fetchIngredients(recipe.id);
            }
        });
    }, [cart]);

    return (
        <div>
            <h1>Your Cart</h1>
            {cart.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <ul>
                    {cart.map((recipe) => (
                        <li key={recipe.id}>
                            <img src={recipe.image} alt={recipe.title} style={{ width: "100px" }} />
                            <div>
                                <h3>{recipe.title}</h3>
                                <button onClick={() => removeFromCart(recipe.id)}>Remove</button>
                                <h4>Ingredients:</h4>
                                <ul>
                                    {ingredients[recipe.id]?.map((ingredient, index) => (
                                        <li key={index}>
                                            {ingredient.amount.us.value} {ingredient.amount.us.unit}{" "}
                                            {ingredient.name}
                                        </li>
                                    )) || <p>Loading ingredients...</p>}
                                </ul>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Cart;
