import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import DOMPurify from "dompurify"; // Para limpiar el HTML si es necesario

function HomeDetails() {
  const { id } = useParams(); // Obtener el ID de la receta desde la URL
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipeDetails = async () => {
      try {
        const response = await fetch(
          `https://api.spoonacular.com/recipes/${id}/information?apiKey=d0fba68ef5204602ac929844f28b7d5f`
        );
        const data = await response.json();
        setRecipe(data);
      } catch (error) {
        setError("Error fetching recipe details");
      } finally {
        setLoading(false);
      }
    };

    fetchRecipeDetails();
  }, [id]); // Dependiendo del ID de la receta

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  // Limpiar el HTML de la descripci贸n si es necesario
  const cleanSummary = DOMPurify.sanitize(recipe.summary);

  return (
    <div className="recipe-details-container">
      <h1>{recipe.title}</h1>
      <img src={recipe.image} alt={recipe.title} />

      <h2>Description</h2>
      <p
        dangerouslySetInnerHTML={{
          __html: cleanSummary, // Renderizar HTML limpio en la descripci贸n
        }}
      />
      
      <h3>Nutrition Info</h3>
      <ul>
        <li><b>Calories:</b> {recipe.nutrition.nutrients.find(nutrient => nutrient.title === 'Calories')?.amount}</li>
        <li><b>Protein:</b> {recipe.nutrition.nutrients.find(nutrient => nutrient.title === 'Protein')?.amount} g</li>
        <li><b>Fat:</b> {recipe.nutrition.nutrients.find(nutrient => nutrient.title === 'Fat')?.amount} g</li>
        <li><b>Carbohydrates:</b> {recipe.nutrition.nutrients.find(nutrient => nutrient.title === 'Carbohydrates')?.amount} g</li>
      </ul>

      <h3>Ingredients</h3>
      <ul>
        {recipe.extendedIngredients.map((ingredient) => (
          <li key={ingredient.id}>{ingredient.original}</li>
        ))}
      </ul>

      <h3>Instructions</h3>
      <p
        dangerouslySetInnerHTML={{
          __html: recipe.instructions,
        }}
      />

      {/* Opcionalmente, puedes agregar otros detalles como el tiempo de preparaci贸n */}
      <h3>Preparation Time</h3>
      <p>{recipe.readyInMinutes} minutes</p>
    </div>
  );
}

export default HomeDetails;
