const RecipeList = () => {
  // ...
  
  const handleApplyFilters = (filters) => {
    let updatedRecipes = [...recipes];

    if (filters.cuisine && filters.cuisine.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.cuisines && recipe.cuisines.some((cuisine) =>
          filters.cuisine.includes(cuisine.toLowerCase())
        )
      );
    }

    if (filters.mealType && filters.mealType.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.dishTypes && recipe.dishTypes.some((type) =>
          filters.mealType.includes(type.toLowerCase())
        )
      );
    }

    if (filters.dietary && filters.dietary.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.diets && recipe.diets.some((diet) =>
          filters.dietary.includes(diet.toLowerCase())
        )
      );
    }

    if (filters.cookingTime && filters.cookingTime.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.readyInMinutes &&
        filters.cookingTime.some((time) => {
          if (time === "short") return recipe.readyInMinutes <= 30;
          if (time === "medium") return recipe.readyInMinutes > 30 && recipe.readyInMinutes <= 60;
          if (time === "long") return recipe.readyInMinutes > 60;
          return false;
        })
      );
    }

    setFilteredRecipes(updatedRecipes);
    setCurrentPage(1);
  };

  return (
    <section className="container-recipes">
      <div className="filters-container">
        <Filters
          onApplyFilters={handleApplyFilters}
          onClearFilters={handleClearFilters}
        />
      </div>

      {/* Resto del c√≥digo */}
    </section>
  );
};

export default RecipeList;
