import React, { useState, useEffect } from "react";
import "./SimilarRecipes.css";

function SimilarRecipes({ ingredients }) {
    const [similarRecipes, setSimilarRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSimilarRecipes = async () => {
            try {
                // Obtener los nombres de los ingredientes para hacer la consulta.
                const ingredientNames = ingredients.map(ingredient => ingredient.name).join(", ");
                
                const response = await fetch(
                    `https://api.spoonacular.com/recipes/findByIngredients?ingredients=${ingredientNames}&number=5&apiKey=d0fba68ef5204602ac929844f28b7d5f`
                );
                
                const data = await response.json();

                if (response.ok) {
                    setSimilarRecipes(data);
                } else {
                    throw new Error(data.message || "Unable to fetch similar recipes.");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        if (ingredients && ingredients.length > 0) {
            fetchSimilarRecipes();
        }
    }, [ingredients]);

    if (loading) {
        return <div>Loading similar recipes...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="similar-recipes-container">
            <h2>Similar Recipes</h2>
            {similarRecipes.length === 0 ? (
                <p>No similar recipes found.</p>
            ) : (
                <div className="similar-recipes-list">
                    {similarRecipes.map((recipe) => (
                        <div className="similar-recipe" key={recipe.id}>
                            <img
                                src={recipe.image}
                                alt={recipe.title}
                                className="similar-recipe-image"
                            />
                            <h3 className="similar-recipe-title">{recipe.title}</h3>
                            <p>{recipe.readyInMinutes} minutes</p>
                            <button className="similar-recipe-btn">View Recipe</button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default SimilarRecipes;
