import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header";
import Home from "./components-home/Home";
import Recipes from "./components-recipes/Recipes"; // Ruta de recetas
import Service from "./components/Service";
import Restaurants from "./components/Restaurants";
import Favorites from "./components/Favorites"; // Página de favoritos
import Cart from "./components/Cart"; // Página de carrito
import LoginPage from "./components/LoginPage"; // Página de login
import HomeDetails from "./components-home/HomeDetails"; // Detalles de receta
import Filters from "./components-recipes/Filters"; // Filtros

import "./App.css";

function App() {
  const [filters, setFilters] = useState({
    cuisine: [],
    mealType: [],
    dietary: [],
    cookingTime: [],
    difficulty: []
  });

  // Función para aplicar filtros
  const handleApplyFilters = (newFilters) => {
    setFilters(newFilters);
    console.log('Filtros aplicados:', newFilters); // Opcional: puedes mostrar los filtros aplicados en consola
  };

  // Función para limpiar los filtros
  const handleClearFilters = () => {
    setFilters({
      cuisine: [],
      mealType: [],
      dietary: [],
      cookingTime: [],
      difficulty: []
    });
    console.log('Filtros limpiados');
  };

  return (
    <Router>
      <Header />
      <Filters onApplyFilters={handleApplyFilters} onClearFilters={handleClearFilters} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/recipes" element={<Recipes filters={filters} />} />
        <Route path="/service" element={<Service />} />
        <Route path="/restaurants" element={<Restaurants />} />
        <Route path="/favorites" element={<Favorites />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/recipe/:recipeId" element={<HomeDetails />} />
      </Routes>
    </Router>
  );
}

export default App;
