import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Home.css";

function HomePopulars() {
    // Estado para almacenar las recetas obtenidas de la API
    const [recipes, setRecipes] = useState([]);

    // Estado para manejar el indicador de carga
    const [loading, setLoading] = useState(true);

    // Estado para manejar errores durante la solicitud de la API
    const [error, setError] = useState(null);

    // Estado para el índice del primer elemento visible en el carrusel
    const [currentIndex, setCurrentIndex] = useState(0);

    // Estado para las recetas que el usuario ha guardado
    const [savedRecipes, setSavedRecipes] = useState([]);

    // Tu clave de API para acceder a Spoonacular
    const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
    const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=1`;

    const navigate = useNavigate(); // Hook para navegar entre rutas

    // Fetch inicial para cargar las recetas desde la API
    useEffect(() => {
        const fetchRecipes = async () => {
            try {
                const response = await fetch(URL);
                const data = await response.json();

                if (response.ok) {
                    // Almacena las recetas en el estado
                    setRecipes(data.recipes);
                } else {
                    throw new Error(data.message || "The recipes could not be loaded.");
                }
            } catch (error) {
                // Guarda el mensaje de error en el estado
                setError(error.message);
            } finally {
                // Marca la carga como completa
                setLoading(false);
            }
        };

        fetchRecipes();
    }, []);

    // Mover el carrusel hacia adelante
    const nextRecipes = () => {
        if (currentIndex + 4 < recipes.length) {
            setCurrentIndex((prevIndex) => prevIndex + 4);
        }
    };

    // Mover el carrusel hacia atrás
    const prevRecipes = () => {
        if (currentIndex > 0) {
            setCurrentIndex((prevIndex) => prevIndex - 4);
        }
    };

    // Agregar o quitar una receta de la lista de guardados
    const toggleSaveRecipe = (id) => {
        setSavedRecipes((prev) =>
            prev.includes(id)
                ? prev.filter((recipeId) => recipeId !== id) // Elimina si ya está guardada
                : [...prev, id] // Agrega si no está guardada
        );
    };

    // Mostrar mensaje de carga si las recetas aún no se han obtenido
    if (loading) {
        return <p>Loading recetas...</p>;
    }

    // Mostrar mensaje de error si ocurrió algún problema al cargar las recetas
    if (error) {
        return <p>{`Error: ${error}`}</p>;
    }

    return (
        <section className="container-populars">
        {/* Contenedor principal del grid */}
        <div className="home-container-populars">
            {/* Contenedor de títulos y subtítulos centrados */}
            <div className="home-populars-texts">
                <h1 className="home-populars-title">
                    Short on time but craving something delicious?
                </h1>
                <p className="home-populars-subtitle">
                    Discover tasty recipes ready in minutes. Cook easy, fast, and full of flavor. Perfect for busy days or last-minute meals.
                </p>
            </div>

            {/* Contenedor de recetas con grid */}
            <div className="recipe-grid">
                {recipes.map((recipe) => (
                    <div
                        key={recipe.id}
                        className="recipe-card"
                        onClick={() => navigate(`/recipe/${recipe.id}`)} // Navegar al detalle de la receta
                    >
                        {/* Contenedor de la receta */}
                        <div className="recipe-image">
                            <img
                                src={recipe.image}
                                alt={recipe.title}
                                className="carousel-image"
                            />
                        </div>

                        {/* Información de la receta */}
                        <div className="recipe-info">
                            <h3 className="carousel-title">{recipe.title}</h3>

                            <div className="carousel-meta">
                                {/* Tiempo de preparación */}
                                <span>
                                    <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
                                </span>

                                {/* Botón de guardar o quitar favorito */}
                                <button
                                    className="save-btn"
                                    onClick={(e) => {
                                        e.stopPropagation(); // Evitar que el clic navegue a la receta
                                        toggleSaveRecipe(recipe.id);
                                    }}
                                >
                                    {savedRecipes.includes(recipe.id) ? (
                                        <i className="bx bxs-heart"></i> // Icono de corazón lleno
                                    ) : (
                                        <i className="bx bx-heart"></i> // Icono de corazón vacío
                                    )}
                                </button>

                                {/* Botón de detalles */}
                                <button className="details-button">
                                    <i className="bx bx-plus"></i>
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    </section>
    );
}

export default HomePopulars;
