import React, { useEffect } from 'react';

function Pagination({ currentPage, setCurrentPage, visiblePages, setVisiblePages, recipes, recipesPerPage }) {
  // Calcula el total de páginas
  const totalPages = Math.ceil(recipes.length / recipesPerPage);

  // Asegura que `visiblePages` tenga al menos 5 elementos
  useEffect(() => {
    if (visiblePages.length < 5) {
      setVisiblePages(Array.from({ length: 5 }, (_, index) => index + 1)); // Inicializa visiblePages con 5 páginas como mínimo
    }
  }, [visiblePages, setVisiblePages]);

  const handleNextPages = () => {
    if (visiblePages[4] < totalPages) {
      setVisiblePages(visiblePages.map((page) => page + 1));
    }
  };

  const handlePrevPages = () => {
    if (visiblePages[0] > 1) {
      setVisiblePages(visiblePages.map((page) => page - 1));
    }
  };

  return (
    <div className="pagination">
      <button onClick={handlePrevPages} disabled={visiblePages[0] === 1}>
        &lt;
      </button>

      {visiblePages.map((page) => (
        <button
          key={page}
          onClick={() => setCurrentPage(page)}
          className={page === currentPage ? 'active' : ''}
        >
          {page}
        </button>
      ))}

      <button
        onClick={handleNextPages}
        disabled={visiblePages[4] >= totalPages}
      >
        &gt;
      </button>
    </div>
  );
}

export default Pagination;
