import React, { useEffect, useState } from "react";
import Filters from "./Filters";
import "./Recipes.css";

function RecipeList() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const API_KEY = "540464a4610b4e4c9488d105323ad0af";

  const fetchRecipes = async (filters = {}) => {
    try {
      setLoading(true);

      const cuisine = filters.cuisine ? filters.cuisine.join(",") : "";
      const mealType = filters.mealType ? filters.mealType.join(",") : "";
      const dietary = filters.dietary ? filters.dietary.join(",") : "";

      let timeQuery = "";
      if (filters.cookingTime) {
        if (filters.cookingTime.includes("under-30")) timeQuery = "&maxReadyTime=30";
        if (filters.cookingTime.includes("30-60")) timeQuery = "&maxReadyTime=60";
      }

      const URL = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&cuisine=${cuisine}&type=${mealType}&diet=${dietary}${timeQuery}`;

      const response = await fetch(URL);
      const data = await response.json();

      if (response.ok) {
        setRecipes(data.results);
      } else {
        throw new Error(data.message || "The recipes could not be loaded.");
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecipes();
  }, []);

  const handleApplyFilters = (filters) => {
    fetchRecipes(filters);
  };

  const handleClearFilters = () => {
    fetchRecipes(); // Fetch all recipes when filters are cleared
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <Filters onApplyFilters={handleApplyFilters} onClearFilters={handleClearFilters} />
      <div className="recipes-container">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card">
            <img src={recipe.image} alt={recipe.title} />
            <h3>{recipe.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecipeList;
