import React, { useState, useEffect } from "react";
import L from "leaflet";
import 'leaflet/dist/leaflet.css';
import "./Restaurants.css";

//const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
const API_KEY = "540464a4610b4e4c9488d105323ad0af"; // Usar esta cuando nos quedemos sin puntos en la otra

function Restaurants() {
  const [location, setLocation] = useState("");
  const [cuisine, setCuisine] = useState("");
  const [diet, setDiet] = useState("");
  const [distance, setDistance] = useState(10);
  const [filters, setFilters] = useState({
    type: "",
    rating: "",
    price: "",
    openNow: false,
    specialFeatures: "",
  });
  const [restaurants, setRestaurants] = useState([]);
  const [page, setPage] = useState(1);

  useEffect (()=> {
    const map = L.map('map').setView([51.505, -0.09], 13);
    console.log("using map");
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "&copy; OpenStreetMap contributors",
    }).addTo(map);
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await fetch(
        `https://api.spoonacular.com/food/restaurants/search?query=${location}&cuisine=${cuisine}&diet=${diet}&distance=${distance}&apiKey=${API_KEY}`
      );
      const data = await response.json();
      setRestaurants(data.restaurants || []);
      setPage(1);
    } catch (error) {
      console.error("Error fetching restaurants:", error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchRestaurants();
  };

  const displayedRestaurants = restaurants.slice((page - 1) * 4, page * 4);
  
  return (
    <div>
    <h1>Find the Best Restaurants</h1>
    <div id="map"></div>
    </div>
  ) 

}

export default Restaurants;
