import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Home.css";

function HomePopulars() {
  const [recipes, setRecipes] = useState([]);  // Estado para almacenar las recetas
  const [loading, setLoading] = useState(true); // Estado para el loading
  const [error, setError] = useState(null);     // Estado para manejar errores

  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f"; // Tu API key
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=5`; // URL para obtener recetas aleatorias

  // Función que obtiene las recetas al cargar el componente
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();
        
        if (response.ok) {
          setRecipes(data.recipes); // Guarda las recetas en el estado
        } else {
          throw new Error(data.message || "Error al cargar las recetas");
        }
      } catch (error) {
        setError(error.message); // Maneja cualquier error
      } finally {
        setLoading(false); // Desactiva el loading una vez se obtienen los datos
      }
    };

    fetchRecipes();
  }, []);

  const [currentIndex, setCurrentIndex] = useState(0);  // Estado para manejar el índice actual del carrusel

  // Funciones para cambiar de receta en el carrusel
  const nextRecipe = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % recipes.length);
  };

  const prevRecipe = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + recipes.length) % recipes.length
    );
  };

  if (loading) {
    return <p>Cargando recetas...</p>;
  }

  if (error) {
    return <p>{`Error: ${error}`}</p>;
  }

  return (
    <section className="container-populars">
      <div className="home-container-choose">
        <h1 className="home-choose-title">Recetas Destacadas</h1>
        <div className="carousel">
          <button className="carousel-btn prev" onClick={prevRecipe}>
            &#10094;
          </button>
          <div className="carousel-item">
            <img
              src={recipes[currentIndex].image}
              alt={recipes[currentIndex].title}
              className="carousel-image"
            />
            <h3 className="carousel-title">{recipes[currentIndex].title}</h3>
            <Link to={`/recipe/${recipes[currentIndex].id}`} className="carousel-link">
              Ver receta
            </Link>
          </div>
          <button className="carousel-btn next" onClick={nextRecipe}>
            &#10095;
          </button>
        </div>
      </div>
    </section>
  );
}

export default HomePopulars;
