import React, { useState, useEffect } from "react";
import L from "leaflet";
import 'leaflet/dist/leaflet.css';
import "./Restaurants.css";

//const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
const API_KEY = "540464a4610b4e4c9488d105323ad0af"; // Usar esta cuando nos quedemos sin puntos en la otra

function Restaurants() {
  const [location, setLocation] = useState("");
  const [cuisine, setCuisine] = useState("");
  const [diet, setDiet] = useState("");
  const [distance, setDistance] = useState(10);
  const [filters, setFilters] = useState({
    type: "",
    rating: "",
    price: "",
    openNow: false,
    specialFeatures: "",
  });
  const [restaurants, setRestaurants] = useState([]);
  const [page, setPage] = useState(1);

  useEffect (()=> {
    const map = L.map('map').setView([51.505, -0.09], 13);
    console.log("using map");
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "&copy; OpenStreetMap contributors",
    }).addTo(map);
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await fetch(
        `https://api.spoonacular.com/food/restaurants/search?query=${location}&cuisine=${cuisine}&diet=${diet}&distance=${distance}&apiKey=${API_KEY}`
      );
      const data = await response.json();
      setRestaurants(data.restaurants || []);
      setPage(1);
    } catch (error) {
      console.error("Error fetching restaurants:", error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchRestaurants();
  };

  const displayedRestaurants = restaurants.slice((page - 1) * 4, page * 4);
  
  return (
    <div>
      <h1>Find the Best Restaurants</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Cuisine type"
          value={cuisine}
          onChange={(e) => setCuisine(e.target.value)}
        />
        <input
          type="text"
          placeholder="Dietary restrictions"
          value={diet}
          onChange={(e) => setDiet(e.target.value)}
        />
        <input
          type="number"
          placeholder="Distance (km)"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      <div>
        <h3>Filters</h3>
        <select onChange={(e) => setFilters({ ...filters, type: e.target.value })}>
          <option value="">Restaurant Type</option>
          <option value="fast food">Fast Food</option>
          <option value="fine dining">Fine Dining</option>
        </select>
        <select onChange={(e) => setFilters({ ...filters, rating: e.target.value })}>
          <option value="">Rating</option>
          <option value="4">4+ Stars</option>
          <option value="3">3+ Stars</option>
        </select>
        <select onChange={(e) => setFilters({ ...filters, price: e.target.value })}>
          <option value="">Price Range</option>
          <option value="$">$</option>
          <option value="$$">$$</option>
          <option value="$$$">$$$</option>
        </select>
        <label>
          <input
            type="checkbox"
            onChange={(e) => setFilters({ ...filters, openNow: e.target.checked })}
          />
          Open Now
        </label>
        <select onChange={(e) => setFilters({ ...filters, specialFeatures: e.target.value })}>
          <option value="">Special Features</option>
          <option value="outdoor seating">Outdoor Seating</option>
          <option value="delivery">Delivery</option>
        </select>
      </div>

      <div id="map" style={{ height: "400px", width: "100%", marginTop: "20px" }}></div>

      <div>
        <h2>Results</h2>
        {displayedRestaurants.length > 0 ? (
          displayedRestaurants.map((restaurant, index) => (
            <div key={index}>
              <h3>{restaurant.name}</h3>
              <p>Rating: {restaurant.rating}</p>
              <p>Price: {restaurant.price}</p>
              <p>Address: {restaurant.address}</p>
            </div>
          ))
        ) : (
          <p>No results found.</p>
        )}
      </div>

      {restaurants.length > 4 && (
        <div>
          <button disabled={page === 1} onClick={() => setPage(page - 1)}>
            Previous
          </button>
          <span> Page {page} </span>
          <button disabled={page * 4 >= restaurants.length} onClick={() => setPage(page + 1)}>
            Next
          </button>
        </div>
      )}
    </div>
  ) 

}

export default Restaurants;
