import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "./components/FavoritesContext";
import { useCart } from "./components/CartContext";
import { useAuth0 } from "@auth0/auth0-react";
import DietaryIcon from "../DietaryIcon"; // Componente de íconos
import "boxicons/css/boxicons.min.css"; // Asegúrate de importar Boxicons
import "./SimilarRecipes.css"; // Estilo específico para las recetas similares

function SimilarRecipes({ recipeId, ingredients }) {
  const [similarRecipes, setSimilarRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSimilarRecipes = async () => {
      try {
        const ingredientString = ingredients.join(",");
        const response = await fetch(
          `https://api.spoonacular.com/recipes/findByIngredients?apiKey=d0fba68ef5204602ac929844f28b7d5f&ingredients=${ingredientString}&number=5`
        );
        const data = await response.json();

        if (response.ok) {
          setSimilarRecipes(data);
        } else {
          throw new Error(data.message || "No similar recipes found.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchSimilarRecipes();
  }, [ingredients]);

  const handleRecipeClick = (recipeId) => {
    navigate(`/recipe/${recipeId}`);
  };

  const handleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      loginWithRedirect();
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  const handleAddToCart = (recipe) => {
    if (!isAuthenticated) {
      loginWithRedirect();
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  if (loading) {
    return <div>Loading similar recipes...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="similar-recipes">
      <h2>Similar Recipes</h2>
      <div className="similar-recipes-grid">
        {similarRecipes.map((recipe) => (
          <div
            key={recipe.id}
            className="recipe-card"
            onClick={() => handleRecipeClick(recipe.id)}
          >
            <img
              src={recipe.image}
              alt={recipe.title}
              className="recipe-card-image"
            />
            <div className="recipe-card-info">
              <h3>{recipe.title}</h3>
              <div className="recipe-card-actions">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleSaveRecipe(recipe);
                  }}
                  aria-label={
                    favorites.some((fav) => fav.id === recipe.id)
                      ? "Remove from saved recipes"
                      : "Save recipe"
                  }
                >
                  <i
                    className={
                      favorites.some((fav) => fav.id === recipe.id)
                        ? "bx bxs-heart"
                        : "bx bx-heart"
                    }
                  ></i>
                </button>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleAddToCart(recipe);
                  }}
                  aria-label={
                    cart.some((item) => item.id === recipe.id)
                      ? "Remove from cart"
                      : "Add to cart"
                  }
                >
                  <i
                    className={
                      cart.some((item) => item.id === recipe.id)
                        ? "bx bxs-plus"
                        : "bx bx-plus"
                    }
                  ></i>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SimilarRecipes;
