import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext"; // Contexto de favoritos
import { useAuth0 } from "@auth0/auth0-react"; // Hook de autenticación
import { useCart } from "../components/CartContext"; // Contexto de carrito
import "./SimilarRecipes.css"; // Cambié el nombre del archivo CSS a SimilarRecipes4.css

function SimilarRecipes({ ingredients }) {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showLoginMessage, setShowLoginMessage] = useState(false);

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const navigate = useNavigate();

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  useEffect(() => {
    const fetchSimilarRecipes = async () => {
      try {
        const ingredientList = ingredients
          .map((ingredient) => ingredient.name)
          .join(",");
        const URL = `https://api.spoonacular.com/recipes/findByIngredients?apiKey=${API_KEY}&ingredients=${ingredientList}&number=16`;
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data);
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (ingredients && ingredients.length > 0) {
      fetchSimilarRecipes();
    }
  }, [ingredients]);

  const nextRecipes = () => {
    if (currentIndex < recipes.length - 4) {
      setCurrentIndex(currentIndex + 4);
    }
  };

  const prevRecipes = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 4);
    }
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  const navigateToRecipe = (recipeId) => {
    navigate(`/recipe/${recipeId}`);
  };

  return (
    <div className="similar-recipes">
      <h2>Similar Recipes</h2>

      {showLoginMessage && (
        <div className="login-message">
          <p>You need to log in to save recipes or add them to your cart.</p>
          <div className="button-container">
            <button className="login-button" onClick={loginWithRedirect}>
              Log In
            </button>
            <button
              className="close-button"
              onClick={() => setShowLoginMessage(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div className="recipe-list">
          {recipes.slice(currentIndex, currentIndex + 4).map((recipe) => (
            <div key={recipe.id} className="recipe-card" onClick={() => navigateToRecipe(recipe.id)}>
              <img src={recipe.image} alt={recipe.title} />
              <div className="recipe-info">
                <h3>{recipe.title}</h3>
                <button onClick={(e) => addToCartHandler(recipe, e)}>
                  <i
                    className={
                      cart.some((item) => item.id === recipe.id)
                        ? "bx bxs-cart-add"
                        : "bx bx-cart-add"
                    }
                  ></i>
                </button>
                <button onClick={() => toggleSaveRecipe(recipe)}>
                  <i
                    className={
                      favorites.some((fav) => fav.id === recipe.id)
                        ? "bx bxs-heart"
                        : "bx bx-heart"
                    }
                  ></i>
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="pagination">
        <button onClick={prevRecipes} disabled={currentIndex === 0}>
          Prev
        </button>
        <button
          onClick={nextRecipes}
          disabled={currentIndex >= recipes.length - 4}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default SimilarRecipes;
