import { useNavigate } from "react-router-dom";

const Cart = () => {
    const { cart, removeFromCart } = useCart();
    const { addFavorite } = useFavorites();
    const [ingredients, setIngredients] = useState({});
    const [servings, setServings] = useState({});
    const [purchasedIngredients, setPurchasedIngredients] = useState({});
    const [steps, setSteps] = useState({});
    const [expand, setExpand] = useState({});
    const [readyTimes, setReadyTimes] = useState({});
    const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";

    const navigate = useNavigate(); // Hook para redirigir a detalles

    const handleRecipeClick = (recipeId) => {
        navigate(`/recipe/${recipeId}`);
    };

    useEffect(() => {
        const fetchInitialIngredients = async (recipeId) => {
            const URL = `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=${API_KEY}`;
            try {
                const response = await fetch(URL);
                const data = await response.json();

                const initialIngredients = data.extendedIngredients
                    .filter((ingredient) => ingredient.amount > 0 && ingredient.unit)
                    .map((ingredient) => ({
                        ...ingredient,
                        amount: ingredient.amount / data.servings,
                    }));

                setIngredients((prev) => ({
                    ...prev,
                    [recipeId]: initialIngredients,
                }));

                setServings((prev) => ({
                    ...prev,
                    [recipeId]: 1,
                }));

                setSteps((prev) => ({
                    ...prev,
                    [recipeId]: data.analyzedInstructions[0]?.steps || [],
                }));

                setReadyTimes((prev) => ({
                    ...prev,
                    [recipeId]: data.readyInMinutes,
                }));

                // Establecer las secciones como desplegadas al inicio
                setExpand((prev) => ({
                    ...prev,
                    [recipeId]: {
                        ingredients: true,
                        steps: true,
                    },
                }));
            } catch (error) {
                console.error("Error fetching initial ingredients:", error);
            }
        };

        cart.forEach((recipe) => {
            if (!ingredients[recipe.id]) {
                fetchInitialIngredients(recipe.id);
            }
        });
    }, [cart]);

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            {cart.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <ul className="cart-list">
                    {cart.map((recipe) => (
                        <li
                            key={recipe.id}
                            className="cart-item"
                            onClick={() => handleRecipeClick(recipe.id)} // Redirigir al hacer clic
                        >
                            <div className="recipe-header">
                                <img src={recipe.image} alt={recipe.title} className="recipe-image" />
                                <div className="recipe-details">
                                    <div className="recipe-title-container">
                                        <h3>{recipe.title}</h3>
                                        <div className="action-buttons">
                                            <button
                                                onClick={(e) => {
                                                    e.stopPropagation(); // Evitar redirección al hacer clic en el botón
                                                    removeFromCart(recipe.id);
                                                }}
                                                className="icon-button delete"
                                                aria-label="Remove from cart"
                                            >
                                                <i className="bx bx-trash"></i>
                                            </button>
                                            <button
                                                onClick={(e) => {
                                                    e.stopPropagation(); // Evitar redirección
                                                    addFavorite(recipe);
                                                }}
                                                className="icon-button favorite"
                                                aria-label="Save to favorites"
                                            >
                                                <i className="bx bx-heart"></i>
                                            </button>
                                        </div>
                                    </div>
                                    <h4>
                                        <i className="bx bx-time-five"></i>
                                        {recipe.readyInMinutes} minutes
                                    </h4>
                                    <div className="servings-control">
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation(); // Evitar redirección
                                                handleServingsChange(recipe.id, -1);
                                            }}
                                            disabled={servings[recipe.id] === 1}
                                        >
                                            -
                                        </button>
                                        <span>Servings: {servings[recipe.id] || 1}</span>
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation(); // Evitar redirección
                                                handleServingsChange(recipe.id, 1);
                                            }}
                                        >
                                            +
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Cart;
