import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useAuth0 } from "@auth0/auth0-react";
import { useCart } from "../components/CartContext";
import "./Home-Populars.css";

function SimilarRecipes({ recipeId }) {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showLoginMessage, setShowLoginMessage] = useState(false);

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const navigate = useNavigate();

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  useEffect(() => {
    const fetchSimilarRecipes = async () => {
      try {
        const URL = `https://api.spoonacular.com/recipes/${recipeId}/similar?apiKey=${API_KEY}&number=16`;
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          setRecipes(data); // La respuesta ya es un array de recetas similares
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (recipeId) {
      fetchSimilarRecipes();
    }
  }, [recipeId]);

  const nextRecipes = () => {
    if (currentIndex + 4 < recipes.length) {
      setCurrentIndex((prevIndex) => Math.min(prevIndex + 4, recipes.length - 4));
    }
  };

  const prevRecipes = () => {
    if (currentIndex > 0) {
      setCurrentIndex((prevIndex) => Math.max(prevIndex - 4, 0));
    }
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      setShowLoginMessage(true);
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading similar recipes...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{`Error: ${error}`}</p>
      </div>
    );
  }

  return (
    <section className="container-populars no-index-padding">
      <div className="home-container-populars">
        <div className="home-populars-texts">
          <h1 className="home-populars-title">Similar Recipes</h1>
          <p className="home-populars-subtitle">
            Explore recipes similar to the one you're viewing.
          </p>
        </div>

        {showLoginMessage && (
          <div className="login-message">
            <p>You need to log in to save recipes or add them to your cart.</p>
            <div className="button-container">
              <button className="login-button" onClick={loginWithRedirect}>
                Log In
              </button>
              <button
                className="close-button"
                onClick={() => setShowLoginMessage(false)}
              >
                Close
              </button>
            </div>
          </div>
        )}

        <div className="carousel-multiple">
          <button
            className="carousel-btn prev"
            onClick={prevRecipes}
            disabled={currentIndex === 0}
          >
            <i className="bx bx-chevron-left"></i>
          </button>

          <div className="carousel-track">
            {recipes.slice(currentIndex, currentIndex + 4).map((recipe) => (
              <div
                key={recipe.id}
                className="carousel-item"
                onClick={() => navigate(`/recipe/${recipe.id}`)}
              >
                <div className="recipe-card">
                  <div className="recipe-image1">
                    {/* Construcción de la URL de la imagen */}
                    <img
                      src={`https://spoonacular.com/recipeImages/${recipe.id}-636x393.${recipe.imageType}`}
                      alt={recipe.title}
                      className="carousel-image"
                    />
                  </div>
                  <div className="recipe-info">
                    <h3 className="carousel-title">{recipe.title}</h3>

                    <div className="carousel-meta">
                      <span>
                        <i className="bx bx-time-five"></i>{" "}
                        {recipe.readyInMinutes ? `${recipe.readyInMinutes} min` : "N/A"}
                      </span>

                      <button
                        className={`save-btn ${
                          favorites.some((fav) => fav.id === recipe.id)
                            ? "saved"
                            : ""
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          toggleSaveRecipe(recipe);
                        }}
                        aria-label={
                          favorites.some((fav) => fav.id === recipe.id)
                            ? "Remove from saved recipes"
                            : "Save recipe"
                        }
                      >
                        <i
                          className={
                            favorites.some((fav) => fav.id === recipe.id)
                              ? "bx bxs-heart"
                              : "bx bx-heart"
                          }
                        ></i>
                      </button>

                      <button
                        className="details-button"
                        onClick={(e) => addToCartHandler(recipe, e)}
                        aria-label={
                          cart.some((item) => item.id === recipe.id)
                            ? "Remove from cart"
                            : "Add to cart"
                        }
                      >
                        <i
                          className={
                            cart.some((item) => item.id === recipe.id)
                              ? "bx bxs-plus"
                              : "bx bx-plus"
                          }
                        ></i>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button
            className="carousel-btn next"
            onClick={nextRecipes}
            disabled={currentIndex + 4 >= recipes.length}
          >
            <i className="bx bx-chevron-right"></i>
          </button>
        </div>
      </div>
    </section>
  );
}

export default SimilarRecipes;
