import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "boxicons/css/boxicons.min.css"; // Importación de iconos

const SimilarRecipes = ({ ingredients }) => {
    const [similarRecipes, setSimilarRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSimilarRecipes = async () => {
            try {
                const ingredientIds = ingredients.map((ingredient) => ingredient.id).join(",");
                const response = await fetch(
                    `https://api.spoonacular.com/recipes/findByIngredients?ingredients=${ingredientIds}&number=5&apiKey=d0fba68ef5204602ac929844f28b7d5f`
                );
                const data = await response.json();

                if (response.ok) {
                    setSimilarRecipes(data);
                } else {
                    throw new Error(data.message || "No similar recipes found.");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        if (ingredients.length > 0) {
            fetchSimilarRecipes();
        }
    }, [ingredients]);

    if (loading) {
        return <div>Loading similar recipes...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <section className="similar-recipes">
            <h2>Similar Recipes</h2>
            <div className="similar-recipes-list">
                {similarRecipes.length > 0 ? (
                    similarRecipes.map((recipe) => (
                        <div key={recipe.id} className="similar-recipe-item">
                            <Link to={`/recipes/${recipe.id}`}>
                                <img src={recipe.image} alt={recipe.title} />
                                <h3>{recipe.title}</h3>
                            </Link>
                        </div>
                    ))
                ) : (
                    <p>No similar recipes found.</p>
                )}
            </div>
        </section>
    );
};

export default SimilarRecipes;
