import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useAuth0 } from "@auth0/auth0-react";
import { useCart } from "../components/CartContext";
import Filters from "./Filters";
import Pagination from "./Pagination";
import "./Recipes.css";

function RecipeList() {
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [recipesPerPage] = useState(20);

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();

  const API_KEY = '540464a4610b4e4c9488d105323ad0af';
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=4`;

  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();
        if (response.ok) {
          setRecipes(data.recipes);
          setFilteredRecipes(data.recipes);
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const handleApplyFilters = (filters) => {
    let updatedRecipes = [...recipes];

    // Filtros existentes
    if (filters.cuisine && filters.cuisine.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.cuisines && recipe.cuisines.some(cuisine => filters.cuisine.includes(cuisine))
      );
    }

    if (filters.mealType && filters.mealType.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.mealType && filters.mealType.includes(recipe.mealType)
      );
    }

    if (filters.dietary && filters.dietary.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.dietary && recipe.dietary.some(diet => filters.dietary.includes(diet))
      );
    }

    if (filters.cookingTime && filters.cookingTime.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.readyInMinutes && filters.cookingTime.includes(recipe.readyInMinutes)
      );
    }

    if (filters.difficulty && filters.difficulty.length > 0) {
      updatedRecipes = updatedRecipes.filter((recipe) =>
        recipe.difficulty && filters.difficulty.includes(recipe.difficulty)
      );
    }

    // Filtro de orden
    if (filters.sortBy === "alphabetical") {
      updatedRecipes = updatedRecipes.sort((a, b) => a.title.localeCompare(b.title));
    } else if (filters.sortBy === "score") {
      updatedRecipes = updatedRecipes.sort((a, b) => b.spoonacularScore - a.spoonacularScore);
    }

    setFilteredRecipes(updatedRecipes);
    setCurrentPage(1); // Resetear a la primera p치gina cuando se apliquen filtros
  };

  const handleClearFilters = () => {
    setFilteredRecipes(recipes);
    setCurrentPage(1); // Resetear a la primera p치gina
  };

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="recipe-list-container">
      <Filters onApplyFilters={handleApplyFilters} onClearFilters={handleClearFilters} />

      <div className="recipe-list">
        {filteredRecipes.slice((currentPage - 1) * recipesPerPage, currentPage * recipesPerPage).map((recipe) => (
          <div key={recipe.id} onClick={() => navigate(`/recipe/${recipe.id}`)}>
            <img src={recipe.image} alt={recipe.title} />
            <h3>{recipe.title}</h3>
            <button onClick={(e) => toggleSaveRecipe(recipe)}>Save to Favorites</button>
            <button onClick={(e) => addToCartHandler(recipe, e)}>
              {cart.some((item) => item.id === recipe.id) ? "Remove from Cart" : "Add to Cart"}
            </button>
          </div>
        ))}
      </div>

      <Pagination
        totalItems={filteredRecipes.length}
        itemsPerPage={recipesPerPage}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />
    </div>
  );
}

export default RecipeList;
