import React from "react";
import { useNavigate } from "react-router-dom"; // Importamos useNavigate
import { useCart } from "./CartContext"; // Contexto del carrito
import "./Cart.css";

const Cart = () => {
  const { cart, removeFromCart } = useCart();
  const navigate = useNavigate(); // Inicializamos useNavigate para la navegación

  // Función para navegar a la página de detalles de la receta
  const goToRecipeDetails = (recipeId) => {
    navigate(`/recipe/${recipeId}`, { state: { fromCart: true } }); // Pasamos el estado fromCart
  };

  return (
    <div>
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty!</p>
      ) : (
        <div className="cart-container">
          {cart.map((recipe) => (
            <div
              key={recipe.id}
              className="cart-item"
              onClick={() => goToRecipeDetails(recipe.id)} // Navegar al hacer clic en todo el contenedor
            >
              <img
                src={recipe.image}
                alt={recipe.title}
                className="cart-image"
              />
              <div className="cart-info">
                <h3>{recipe.title}</h3> {/* El título sigue siendo estático */}
                <h4>
                  <i className="bx bx-time-five"></i>
                  {recipe.readyInMinutes} minutes
                </h4>
                <p>
                  {recipe.summary
                    ? recipe.summary.replace(/(<([^>]+)>)/gi, "")
                    : "No description available."}
                </p>
              </div>
              <div className="cart-buttons">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromCart(recipe.id);
                  }}
                >
                  <i className="bx bx-trash"></i>
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
