// src/components-recipes/RecipeList.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';  // Importar hook de Auth0

function RecipeList({ recipes, addToCartHandler, toggleSaveRecipe, favorites, cart }) {
  const navigate = useNavigate();
  const { isAuthenticated, loginWithRedirect } = useAuth0();  // Usar para verificar si está autenticado
  const [showLoginMessage, setShowLoginMessage] = useState(false); // Estado para mostrar el mensaje

  const handleSaveRecipe = (e, recipe) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      // Si no está autenticado, mostrar el mensaje de inicio de sesión
      setShowLoginMessage(true);
      return;
    }
    toggleSaveRecipe(recipe);  // Si está autenticado, guardar receta
  };

  const handleAddToCart = (e, recipe) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      // Si no está autenticado, mostrar el mensaje de inicio de sesión
      setShowLoginMessage(true);
      return;
    }
    addToCartHandler(recipe, e);  // Si está autenticado, agregar al carrito
  };

  const handleBackToRecipes = () => {
    navigate('/recipes'); // Redirigir a la página de recetas
  };

  return (
    <div>
      {showLoginMessage && (
        <div className="login-message">
          <p>You need to log in to save recipes or add them to your cart.</p>
          <div className="button-container">
            <button
              className="login-button"
              onClick={loginWithRedirect} // Redirige al login de Auth0
            >
              Log In
            </button>
            <button
              className="close-button"
              onClick={handleBackToRecipes} // Redirige a la lista de recetas
            >
              Back to Recipes
            </button>
          </div>
        </div>
      )}

      <div className="recipes-grid">
        {recipes.map((recipe) => (
          <div
            key={recipe.id}
            className="recipe-card"
            onClick={() => navigate(`/recipe/${recipe.id}`)}  // Redirigir a la página de detalles
          >
            <div className="recipe-image1">
              <img
                src={recipe.image}
                alt={recipe.title}
                className="recipe-image"
              />
            </div>
            <div className="recipe-info">
              <h3 className="recipe-title">{recipe.title}</h3>
              <div className="recipe-meta">
                <span>
                  <i className="bx bx-time-five"></i>{' '}
                  {recipe.readyInMinutes !== undefined
                    ? recipe.readyInMinutes
                    : 'N/A'}{' '}
                  min
                </span>

                {/* Botón para guardar receta */}
                <button
                  className={`save-btn ${
                    favorites.some((fav) => fav.id === recipe.id) ? 'saved' : ''
                  }`}
                  onClick={(e) => handleSaveRecipe(e, recipe)}  // Llamar a la función para guardar la receta
                >
                  <i
                    className={
                      favorites.some((fav) => fav.id === recipe.id)
                        ? 'bx bxs-heart'
                        : 'bx bx-heart'
                    }
                  ></i>
                </button>

                {/* Botón para agregar al carrito */}
                <button
                  className="details-button"
                  onClick={(e) => handleAddToCart(e, recipe)}  // Llamar a la función para agregar al carrito
                >
                  <i
                    className={
                      cart.some((item) => item.id === recipe.id)
                        ? 'bx bxs-plus'
                        : 'bx bx-plus'
                    }
                  ></i>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecipeList;
